import{_ as a,c as i,o as t,ae as n}from"./chunks/framework.Dh1jimFm.js";const g=JSON.parse('{"title":"Admin Interface","description":"","frontmatter":{},"headers":[],"relativePath":"user-guide/admin-interface.md","filePath":"user-guide/admin-interface.md"}'),s={name:"user-guide/admin-interface.md"};function r(l,e,o,m,u,d){return t(),i("div",null,e[0]||(e[0]=[n('<h1 id="admin-interface" tabindex="-1">Admin Interface <a class="header-anchor" href="#admin-interface" aria-label="Permalink to &quot;Admin Interface&quot;">​</a></h1><p>The Adminbolt Admin Interface provides server administrators with powerful tools to manage the entire hosting platform. This interface is designed to simplify complex server management tasks and provide a clear overview of system health and status.</p><h2 id="dashboard" tabindex="-1">Dashboard <a class="header-anchor" href="#dashboard" aria-label="Permalink to &quot;Dashboard&quot;">​</a></h2><p>The admin dashboard provides:</p><ul><li>System status and health indicators</li><li>Resource usage statistics</li><li>Recent activity logs</li><li>Quick access to common tasks</li></ul><h2 id="server-management" tabindex="-1">Server Management <a class="header-anchor" href="#server-management" aria-label="Permalink to &quot;Server Management&quot;">​</a></h2><p>Tools for managing core server components:</p><ul><li>Install and configure system services</li><li>Manage PHP versions and extensions</li><li>Configure web servers (Apache/Nginx)</li><li>Manage mail servers</li><li>Configure DNS servers</li></ul><h2 id="customer-management" tabindex="-1">Customer Management <a class="header-anchor" href="#customer-management" aria-label="Permalink to &quot;Customer Management&quot;">​</a></h2><p>Manage hosting customers and their accounts:</p><ul><li>Create and manage customer accounts</li><li>Configure hosting plans and quotas</li><li>View customer activity and resource usage</li><li>Access customer accounts</li></ul><h2 id="domain-management" tabindex="-1">Domain Management <a class="header-anchor" href="#domain-management" aria-label="Permalink to &quot;Domain Management&quot;">​</a></h2><p>Tools for managing domains and DNS:</p><ul><li>View and manage all domains</li><li>Configure DNS servers</li><li>Manage SSL certificates</li><li>Set up domain redirects</li></ul><h2 id="email-management" tabindex="-1">Email Management <a class="header-anchor" href="#email-management" aria-label="Permalink to &quot;Email Management&quot;">​</a></h2><p>Email server administration tools:</p><ul><li>Configure mail servers</li><li>Manage domain-wide email settings</li><li>View email server statistics</li><li>Configure spam protection</li></ul><h2 id="database-management" tabindex="-1">Database Management <a class="header-anchor" href="#database-management" aria-label="Permalink to &quot;Database Management&quot;">​</a></h2><p>Database server administration:</p><ul><li>Configure database servers</li><li>Manage database backups</li><li>View database performance metrics</li><li>Optimize database performance</li></ul><h2 id="security-management" tabindex="-1">Security Management <a class="header-anchor" href="#security-management" aria-label="Permalink to &quot;Security Management&quot;">​</a></h2><p>Tools to secure the hosting platform:</p><ul><li>Configure firewall rules</li><li>Manage IP blocking and whitelisting</li><li>Configure Fail2Ban settings</li><li>Run security scans</li><li>View security reports</li></ul><h2 id="backup-management" tabindex="-1">Backup Management <a class="header-anchor" href="#backup-management" aria-label="Permalink to &quot;Backup Management&quot;">​</a></h2><p>Manage system and customer backups:</p><ul><li>Configure backup schedules</li><li>Manage backup storage</li><li>Restore from backups</li><li>Monitor backup status</li></ul><h2 id="system-updates" tabindex="-1">System Updates <a class="header-anchor" href="#system-updates" aria-label="Permalink to &quot;System Updates&quot;">​</a></h2><p>Keep the system up to date:</p><ul><li>Check for updates</li><li>Apply system updates</li><li>View update history</li></ul><h2 id="system-settings" tabindex="-1">System Settings <a class="header-anchor" href="#system-settings" aria-label="Permalink to &quot;System Settings&quot;">​</a></h2><p>Configure global system settings:</p><ul><li>Server network configuration</li><li>System notifications</li><li>Default service settings</li><li>Branding and customization</li></ul>',32)]))}const h=a(s,[["render",r]]);export{g as __pageData,h as default};
