import{_ as t,c as a,o as l,ag as o}from"./chunks/framework.Ds6Eueu6.js";const s="/screenshots/myapache-access-logs.png",r="/screenshots/apache-eye-icon.png",p=JSON.parse('{"title":"MyApache Access Logs","description":"","frontmatter":{},"headers":[{"level":3,"title":"Search and Filters","slug":"search-and-filters","link":"#search-and-filters","children":[]},{"level":3,"title":"Log Table","slug":"log-table","link":"#log-table","children":[]},{"level":3,"title":"Truncate Logs","slug":"truncate-logs","link":"#truncate-logs","children":[]},{"level":3,"title":"View Details Modal","slug":"view-details-modal","link":"#view-details-modal","children":[]}],"relativePath":"user-guide/admin-panel/apache-access-logs.md","filePath":"user-guide/admin-panel/apache-access-logs.md"}'),i={name:"user-guide/admin-panel/apache-access-logs.md"};function n(c,e,d,g,h,u){return l(),a("div",null,e[0]||(e[0]=[o('<h1 id="myapache-access-logs" tabindex="-1">MyApache Access Logs <a class="header-anchor" href="#myapache-access-logs" aria-label="Permalink to &quot;MyApache Access Logs&quot;">​</a></h1><p>The <strong>MyApache Access Logs</strong> page provides a real-time overview of all HTTP requests made to domains managed by the MyApache module.</p><p><img src="'+s+'" alt="MyApache Access Logs"></p><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p><strong>URL:</strong> <code>/admin/my-apache-access-logs</code></p><p>This section allows administrators to monitor, search, and filter incoming HTTP requests in real time. It helps in debugging issues, tracking malicious requests, and understanding traffic patterns.</p><h2 id="interface-elements" tabindex="-1">Interface Elements <a class="header-anchor" href="#interface-elements" aria-label="Permalink to &quot;Interface Elements&quot;">​</a></h2><h3 id="search-and-filters" tabindex="-1">Search and Filters <a class="header-anchor" href="#search-and-filters" aria-label="Permalink to &quot;Search and Filters&quot;">​</a></h3><p>Located at the top right above the log table:</p><ul><li><strong>Search Bar</strong> – Search any keyword from the logs (e.g., IP, URL, method).</li><li><strong>Filters Panel</strong> (accessible via the <em>Filters</em> button): <ul><li><strong>Source</strong> – Filter logs by domain.</li><li><strong>Level</strong> – Filter by log level (e.g., INFO, DEBUG, ERROR).</li><li><strong>Date Range</strong> – Set <code>From</code> and <code>Until</code> dates to filter by time.</li></ul></li></ul><h3 id="log-table" tabindex="-1">Log Table <a class="header-anchor" href="#log-table" aria-label="Permalink to &quot;Log Table&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Column</th><th>Description</th></tr></thead><tbody><tr><td><strong>Time</strong></td><td>Timestamp of the HTTP request.</td></tr><tr><td><strong>Source</strong></td><td>Domain or server IP that handled the request.</td></tr><tr><td><strong>Level</strong></td><td>Log severity level (INFO, WARN, etc.).</td></tr><tr><td><strong>Message</strong></td><td>Full HTTP request message including method, status code, etc.</td></tr><tr><td><strong>View</strong></td><td>Button to see full log message in detail.</td></tr></tbody></table><h2 id="actions" tabindex="-1">Actions <a class="header-anchor" href="#actions" aria-label="Permalink to &quot;Actions&quot;">​</a></h2><h3 id="truncate-logs" tabindex="-1"><code>Truncate Logs</code> <a class="header-anchor" href="#truncate-logs" aria-label="Permalink to &quot;`Truncate Logs`&quot;">​</a></h3><ul><li>Orange button at the top-right corner.</li><li>Deletes all current log entries from the view.</li><li>Action requires confirmation.</li></ul><h2 id="pagination" tabindex="-1">Pagination <a class="header-anchor" href="#pagination" aria-label="Permalink to &quot;Pagination&quot;">​</a></h2><p>At the bottom of the table:</p><ul><li>Choose results per page (e.g., 10, 25, 50, All).</li><li>Navigate through multiple pages of logs.</li></ul><h3 id="view-details-modal" tabindex="-1">View Details Modal <a class="header-anchor" href="#view-details-modal" aria-label="Permalink to &quot;View Details Modal&quot;">​</a></h3><p>Each log entry in the table has a <strong>View</strong> button with an eye icon</p><p>Clicking this icon opens a modal window with the full log details:</p><p><img src="'+r+'" alt="View Log Modal"></p><p>Inside the modal, you can see:</p><ul><li><strong>Created at</strong> – Timestamp when the log was recorded</li><li><strong>Source</strong> – Hostname or IP address of the request</li><li><strong>Level</strong> – Log level (e.g. INFO)</li><li><strong>Message</strong> – Complete HTTP request string, including user agent and response code</li></ul><h2 id="notes" tabindex="-1">Notes <a class="header-anchor" href="#notes" aria-label="Permalink to &quot;Notes&quot;">​</a></h2><ul><li>All logs are generated in real-time.</li><li>Logs can be filtered by custom time range for deeper analysis.</li><li>Useful for both debugging and security monitoring.</li></ul>',26)]))}const b=t(i,[["render",n]]);export{p as __pageData,b as default};
