import{_ as a,c as i,o as t,a2 as o}from"./chunks/framework.CHomSxuB.js";const n="/screenshots/myapache-core-settings.png",r="/screenshots/apache-logging.png",l="/screenshots/apache-performance.png",s="/screenshots/apache-external-services.png",f=JSON.parse('{"title":"MyApache ModSecurity Configuration","description":"","frontmatter":{},"headers":[{"level":3,"title":"ModSecurity Status","slug":"modsecurity-status","link":"#modsecurity-status","children":[]},{"level":3,"title":"Rules Engine","slug":"rules-engine","link":"#rules-engine","children":[]},{"level":3,"title":"Connection Engine","slug":"connection-engine","link":"#connection-engine","children":[]},{"level":3,"title":"Audit Logging","slug":"audit-logging","link":"#audit-logging","children":[]},{"level":3,"title":"Guardian Log Path","slug":"guardian-log-path","link":"#guardian-log-path","children":[]},{"level":3,"title":"Backend Compression","slug":"backend-compression","link":"#backend-compression","children":[]},{"level":3,"title":"PCRE Match Limit","slug":"pcre-match-limit","link":"#pcre-match-limit","children":[]},{"level":3,"title":"PCRE Match Recursion Limit","slug":"pcre-match-recursion-limit","link":"#pcre-match-recursion-limit","children":[]},{"level":3,"title":"GeoIP Database Path","slug":"geoip-database-path","link":"#geoip-database-path","children":[]},{"level":3,"title":"Google Safe Browsing Database","slug":"google-safe-browsing-database","link":"#google-safe-browsing-database","children":[]},{"level":3,"title":"Project Honey Pot API Key","slug":"project-honey-pot-api-key","link":"#project-honey-pot-api-key","children":[]}],"relativePath":"user-guide/admin-panel/apache-modsecurity-config.md","filePath":"user-guide/admin-panel/apache-modsecurity-config.md"}'),c={name:"user-guide/admin-panel/apache-modsecurity-config.md"};function g(u,e,d,h,p,m){return t(),i("div",null,e[0]||(e[0]=[o('<h1 id="myapache-modsecurity-configuration" tabindex="-1">MyApache ModSecurity Configuration <a class="header-anchor" href="#myapache-modsecurity-configuration" aria-label="Permalink to &quot;MyApache ModSecurity Configuration&quot;">​</a></h1><p>The <strong>MyApache ModSecurity Configuration</strong> page allows administrators to configure ModSecurity, a powerful web application firewall (WAF) integrated with Apache. This interface provides full control over rule enforcement, connection handling, logging behavior, performance tuning, and external data integration.</p><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p>ModSecurity helps detect and block common web exploits and threats in real time. This page offers a structured and modular approach to manage:</p><ul><li>WAF activation and rule processing</li><li>Logging and auditing</li><li>Resource and performance tuning</li><li>External threat data integration</li></ul><p><strong>URL:</strong> <code>/admin/my-apache-mod-security-config</code></p><h2 id="core-settings" tabindex="-1">Core Settings <a class="header-anchor" href="#core-settings" aria-label="Permalink to &quot;Core Settings&quot;">​</a></h2><p><img src="'+n+'" alt="Core Settings"></p><h3 id="modsecurity-status" tabindex="-1">ModSecurity Status <a class="header-anchor" href="#modsecurity-status" aria-label="Permalink to &quot;ModSecurity Status&quot;">​</a></h3><ul><li><strong>Enabled (Production)</strong> – Activates ModSecurity globally.</li><li><strong>Disabled</strong> – Turns off all ModSecurity processing.</li></ul><h3 id="rules-engine" tabindex="-1">Rules Engine <a class="header-anchor" href="#rules-engine" aria-label="Permalink to &quot;Rules Engine&quot;">​</a></h3><ul><li><strong>Process rules (Production)</strong> – Full rule evaluation and enforcement.</li><li><strong>Detection mode only</strong> – Rules are processed but not enforced (log only).</li><li><strong>Disabled</strong> – Rule engine is completely turned off.</li></ul><h3 id="connection-engine" tabindex="-1">Connection Engine <a class="header-anchor" href="#connection-engine" aria-label="Permalink to &quot;Connection Engine&quot;">​</a></h3><ul><li><strong>Process rules</strong> – Applies rules at the connection level.</li><li><strong>Detection mode only</strong> – Logs matching rules without enforcement.</li><li><strong>Disabled</strong> – Disables connection-level processing.</li></ul><h2 id="logging-auditing" tabindex="-1">Logging &amp; Auditing <a class="header-anchor" href="#logging-auditing" aria-label="Permalink to &quot;Logging &amp; Auditing&quot;">​</a></h2><p><img src="'+r+'" alt="Logging &amp; Auditing"></p><h3 id="audit-logging" tabindex="-1">Audit Logging <a class="header-anchor" href="#audit-logging" aria-label="Permalink to &quot;Audit Logging&quot;">​</a></h3><ul><li><strong>Log relevant transactions only</strong> – Logs only suspicious/matching requests.</li><li><strong>Log all transactions</strong> – Logs all HTTP traffic.</li><li><strong>Disable logging</strong> – Disables logging entirely.</li></ul><h3 id="guardian-log-path" tabindex="-1">Guardian Log Path <a class="header-anchor" href="#guardian-log-path" aria-label="Permalink to &quot;Guardian Log Path&quot;">​</a></h3><ul><li>Default: <code>/var/log/modsec_guardian.log</code></li><li>Stores audit logs for ModSecurity’s Guardian integration.</li></ul><h2 id="performance" tabindex="-1">Performance <a class="header-anchor" href="#performance" aria-label="Permalink to &quot;Performance&quot;">​</a></h2><p><img src="'+l+'" alt="Performance"></p><h3 id="backend-compression" tabindex="-1">Backend Compression <a class="header-anchor" href="#backend-compression" aria-label="Permalink to &quot;Backend Compression&quot;">​</a></h3><ul><li><strong>Enabled</strong> – Enables compression for backend data transfer.</li><li><strong>Disabled</strong> – Disables backend compression.</li></ul><h3 id="pcre-match-limit" tabindex="-1">PCRE Match Limit <a class="header-anchor" href="#pcre-match-limit" aria-label="Permalink to &quot;PCRE Match Limit&quot;">​</a></h3><ul><li>Default: <code>1500</code></li><li>Controls the maximum number of pattern matches processed.</li></ul><h3 id="pcre-match-recursion-limit" tabindex="-1">PCRE Match Recursion Limit <a class="header-anchor" href="#pcre-match-recursion-limit" aria-label="Permalink to &quot;PCRE Match Recursion Limit&quot;">​</a></h3><ul><li>Default: <code>1500</code></li><li>Controls recursion depth for pattern matching.</li></ul><h2 id="external-services" tabindex="-1">External Services <a class="header-anchor" href="#external-services" aria-label="Permalink to &quot;External Services&quot;">​</a></h2><p><img src="'+s+'" alt="External Services"></p><h3 id="geoip-database-path" tabindex="-1">GeoIP Database Path <a class="header-anchor" href="#geoip-database-path" aria-label="Permalink to &quot;GeoIP Database Path&quot;">​</a></h3><ul><li>Default: <code>/usr/share/GeoIP/GeoLite2-City.mmdb</code></li><li>Used for geolocation-based rule evaluation.</li></ul><h3 id="google-safe-browsing-database" tabindex="-1">Google Safe Browsing Database <a class="header-anchor" href="#google-safe-browsing-database" aria-label="Permalink to &quot;Google Safe Browsing Database&quot;">​</a></h3><ul><li>Path: <code>/var/lib/modsecurity/gsb.db</code></li><li>Protects against malicious URLs and phishing domains.</li></ul><h3 id="project-honey-pot-api-key" tabindex="-1">Project Honey Pot API Key <a class="header-anchor" href="#project-honey-pot-api-key" aria-label="Permalink to &quot;Project Honey Pot API Key&quot;">​</a></h3><ul><li>Optional integration for real-time spammer and bot detection.</li><li>Requires a valid HTTP:BL API key.</li></ul><h2 id="notes" tabindex="-1">Notes <a class="header-anchor" href="#notes" aria-label="Permalink to &quot;Notes&quot;">​</a></h2><ul><li>Changes require saving via the <strong>Save Settings</strong> button in each tab.</li><li>ModSecurity is a critical layer of security—configuration should be tested before applying in production.</li><li>For advanced users, logs and rules can be fine-tuned further via CLI or rule set customization.</li></ul>',38)]))}const y=a(c,[["render",g]]);export{f as __pageData,y as default};
