import{_ as t,c as a,o as i,a2 as s}from"./chunks/framework.CHomSxuB.js";const o="/screenshots/api-keys.png",l="/screenshots/api-keys-create.png",r="/screenshots/api-keys-edit.png",m=JSON.parse('{"title":"API Keys","description":"","frontmatter":{},"headers":[{"level":3,"title":"Table Columns","slug":"table-columns","link":"#table-columns","children":[]},{"level":3,"title":"Table Features","slug":"table-features","link":"#table-features","children":[]},{"level":3,"title":"Actions","slug":"actions","link":"#actions","children":[]},{"level":3,"title":"API Key Details","slug":"api-key-details","link":"#api-key-details","children":[]},{"level":3,"title":"Security Settings","slug":"security-settings","link":"#security-settings","children":[]},{"level":3,"title":"Footer Actions","slug":"footer-actions","link":"#footer-actions","children":[]}],"relativePath":"user-guide/admin-panel/api-keys.md","filePath":"user-guide/admin-panel/api-keys.md"}'),n={name:"user-guide/admin-panel/api-keys.md"};function d(c,e,g,u,h,y){return i(),a("div",null,e[0]||(e[0]=[s('<h1 id="api-keys" tabindex="-1">API Keys <a class="header-anchor" href="#api-keys" aria-label="Permalink to &quot;API Keys&quot;">​</a></h1><p>The <strong>API Keys</strong> section allows administrators to create, view, edit, and manage secure API keys for authenticated system integrations. Each key can be restricted by IP address and toggled on or off individually.</p><p><img src="'+o+'" alt="API Keys Overview"></p><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p><strong>URL:</strong> <code>/admin/api-keys</code></p><p>This page provides a searchable and paginated table of all existing API Keys.</p><h3 id="table-columns" tabindex="-1">Table Columns <a class="header-anchor" href="#table-columns" aria-label="Permalink to &quot;Table Columns&quot;">​</a></h3><ul><li><strong>Name</strong> – A user-defined name for the API Key (e.g., &quot;AdminBolt&quot;).</li><li><strong>API Key</strong> – The public token used for authentication.</li><li><strong>API Secret</strong> – The private secret used alongside the key. This value is hidden by default.</li></ul><h3 id="table-features" tabindex="-1">Table Features <a class="header-anchor" href="#table-features" aria-label="Permalink to &quot;Table Features&quot;">​</a></h3><ul><li><strong>Search</strong> – You can search the list using any of the following fields: <ul><li><strong>Name</strong></li><li><strong>API Key</strong></li><li><strong>API Secret</strong></li></ul></li><li><strong>Pagination</strong> – A dropdown at the bottom of the table allows you to select how many results to display: <ul><li><strong>5</strong>, <strong>10</strong>, <strong>25</strong>, <strong>50</strong>, or <strong>All</strong> results per page.</li></ul></li></ul><h3 id="actions" tabindex="-1">Actions <a class="header-anchor" href="#actions" aria-label="Permalink to &quot;Actions&quot;">​</a></h3><ul><li><strong>Create API Key</strong> – Opens a modal form for creating a new key.</li><li><strong>Edit</strong> – Opens the edit modal for an existing key.</li><li><strong>Delete</strong> – Permanently removes the selected key.</li></ul><h2 id="create-edit-api-key" tabindex="-1">Create/Edit API Key <a class="header-anchor" href="#create-edit-api-key" aria-label="Permalink to &quot;Create/Edit API Key&quot;">​</a></h2><p>The modal form for creating or editing an API key is divided into two sections: <strong>API Key Details</strong> and <strong>Security Settings</strong>.</p><p><img src="'+l+'" alt="Create API Key Modal"><br><img src="'+r+'" alt="Edit API Key Modal"></p><h3 id="api-key-details" tabindex="-1">API Key Details <a class="header-anchor" href="#api-key-details" aria-label="Permalink to &quot;API Key Details&quot;">​</a></h3><ul><li><strong>Name</strong>* – A friendly label for the key.</li><li><strong>API Key</strong> – Generated automatically and used for authentication.</li><li><strong>API Secret</strong> – Also generated automatically.</li></ul><h3 id="security-settings" tabindex="-1">Security Settings <a class="header-anchor" href="#security-settings" aria-label="Permalink to &quot;Security Settings&quot;">​</a></h3><ul><li><strong>API Key Enabled</strong> – Toggle to activate or deactivate the key.</li><li><strong>IP Restriction</strong> – Toggle to enable IP restriction. <ul><li><strong>Whitelisted IPs</strong> – A list of IP addresses from which requests will be accepted. <ul><li>Format: Add one or more IPs using the input field.</li></ul></li></ul></li></ul><h3 id="footer-actions" tabindex="-1">Footer Actions <a class="header-anchor" href="#footer-actions" aria-label="Permalink to &quot;Footer Actions&quot;">​</a></h3><ul><li><strong>Create</strong> – Saves the API Key and closes the modal.</li><li><strong>Create &amp; create another</strong> – Saves the key and reopens the form.</li><li><strong>Save Changes</strong> – Updates the existing key.</li><li><strong>Cancel</strong> – Closes the modal without saving.</li></ul><h2 id="notes" tabindex="-1">Notes <a class="header-anchor" href="#notes" aria-label="Permalink to &quot;Notes&quot;">​</a></h2><ul><li><strong>Security Tip:</strong> Use IP restrictions whenever possible for enhanced protection.</li></ul>',23)]))}const f=t(n,[["render",d]]);export{m as __pageData,f as default};
