import{_ as t,c as i,o as a,a2 as r}from"./chunks/framework.CHomSxuB.js";const o="/screenshots/certificates.png",s="/screenshots/import-certificate.png",n="/screenshots/edit-certificate.png",g=JSON.parse('{"title":"Certificates","description":"","frontmatter":{},"headers":[{"level":3,"title":"Import Certificate","slug":"import-certificate","link":"#import-certificate","children":[]},{"level":3,"title":"Edit Certificate","slug":"edit-certificate","link":"#edit-certificate","children":[]},{"level":3,"title":"Additional Actions","slug":"additional-actions","link":"#additional-actions","children":[]}],"relativePath":"user-guide/admin-panel/certificates.md","filePath":"user-guide/admin-panel/certificates.md"}'),c={name:"user-guide/admin-panel/certificates.md"};function l(d,e,h,f,p,m){return a(),i("div",null,e[0]||(e[0]=[r('<h1 id="certificates" tabindex="-1">Certificates <a class="header-anchor" href="#certificates" aria-label="Permalink to &quot;Certificates&quot;">​</a></h1><p>The <strong>Certificates</strong> page in AdminBolt allows administrators to manage, import, edit, and download SSL/TLS certificates for their domains and services.</p><p><img src="'+o+'" alt="Certificates Overview"></p><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p>This page provides an overview of all active SSL/TLS certificates currently installed on the system. Each certificate entry shows:</p><ul><li><strong>Domain</strong> — The main domain the certificate is issued for (e.g. <code>157-180-40-140.myboltip.com</code>)</li><li><strong>Provider</strong> — The certificate authority (e.g. ZeroSSL)</li><li><strong>Valid From / To</strong> — The certificate&#39;s validity period</li><li><strong>Is Active</strong> — Badge indicating whether the certificate is currently active</li></ul><h2 id="url" tabindex="-1">URL <a class="header-anchor" href="#url" aria-label="Permalink to &quot;URL&quot;">​</a></h2><p><code>/admin/certificates</code></p><h2 id="actions" tabindex="-1">Actions <a class="header-anchor" href="#actions" aria-label="Permalink to &quot;Actions&quot;">​</a></h2><h3 id="import-certificate" tabindex="-1">Import Certificate <a class="header-anchor" href="#import-certificate" aria-label="Permalink to &quot;Import Certificate&quot;">​</a></h3><p>Clicking the <strong>Import Certificate</strong> button opens a modal that allows you to manually paste PEM-formatted certificate data.</p><p><img src="'+s+'" alt="Import Certificate Modal"></p><p>The modal includes the following fields:</p><ul><li><strong>Certificate (PEM)</strong> – Required</li><li><strong>Private Key (PEM)</strong> – Required</li><li><strong>Certificate Chain (PEM)</strong> – Optional</li></ul><p>Once all required fields are filled in, click <strong>Submit</strong> to import the certificate into the system.</p><h3 id="edit-certificate" tabindex="-1">Edit Certificate <a class="header-anchor" href="#edit-certificate" aria-label="Permalink to &quot;Edit Certificate&quot;">​</a></h3><p>Each certificate can be edited by clicking the <strong>Edit</strong> icon in its row. This opens a modal where all associated data can be updated.</p><p><img src="'+n+'" alt="Edit Certificate Modal"></p><p>Editable fields:</p><ul><li><strong>Certificate (PEM)</strong> – Modify the main certificate content</li><li><strong>Private Key (PEM)</strong> – Update the private key</li><li><strong>Certificate Chain (PEM)</strong> – Update the certificate chain if needed</li></ul><p>Click <strong>Save Changes</strong> to apply updates.</p><h3 id="additional-actions" tabindex="-1">Additional Actions <a class="header-anchor" href="#additional-actions" aria-label="Permalink to &quot;Additional Actions&quot;">​</a></h3><ul><li><strong>Delete</strong> – Permanently removes the certificate from the system.</li><li><strong>Download</strong> – Exports the certificate (likely in PEM format) for local backup or use on other systems.</li></ul><h2 id="notes" tabindex="-1">Notes <a class="header-anchor" href="#notes" aria-label="Permalink to &quot;Notes&quot;">​</a></h2><ul><li>The system supports multiple domains (SAN) in a single certificate.</li></ul>',25)]))}const v=t(c,[["render",l]]);export{g as __pageData,v as default};
