import{_ as t,c as a,o as n,ag as o}from"./chunks/framework.Ds6Eueu6.js";const s="/screenshots/change-hostname.png",p=JSON.parse('{"title":"Change System Hostname","description":"","frontmatter":{},"headers":[{"level":3,"title":"Current Hostname","slug":"current-hostname","link":"#current-hostname","children":[]},{"level":3,"title":"New Hostname","slug":"new-hostname","link":"#new-hostname","children":[]},{"level":3,"title":"Info Tooltip","slug":"info-tooltip","link":"#info-tooltip","children":[]},{"level":3,"title":"Change Hostname Button","slug":"change-hostname-button","link":"#change-hostname-button","children":[]}],"relativePath":"user-guide/admin-panel/change-hostname.md","filePath":"user-guide/admin-panel/change-hostname.md"}'),i={name:"user-guide/admin-panel/change-hostname.md"};function r(l,e,h,m,u,d){return n(),a("div",null,e[0]||(e[0]=[o('<h1 id="change-system-hostname" tabindex="-1">Change System Hostname <a class="header-anchor" href="#change-system-hostname" aria-label="Permalink to &quot;Change System Hostname&quot;">​</a></h1><p>The <strong>Change Hostname</strong> page allows you to update the system&#39;s hostname.</p><p><img src="'+s+'" alt="Change Hostname Page"></p><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p><strong>URL:</strong> <code>/admin/change-hostname</code></p><p>This page enables administrators to modify the hostname.</p><p>The hostname is a key component of your server&#39;s identity and is used in outgoing mail headers and various system services. It should be a Fully Qualified Domain Name (FQDN), such as <code>host.example.com</code>.</p><h3 id="current-hostname" tabindex="-1">Current Hostname <a class="header-anchor" href="#current-hostname" aria-label="Permalink to &quot;Current Hostname&quot;">​</a></h3><p>Displays the currently configured hostname for your server.</p><h3 id="new-hostname" tabindex="-1">New Hostname <a class="header-anchor" href="#new-hostname" aria-label="Permalink to &quot;New Hostname&quot;">​</a></h3><p>Input field for entering the new hostname.</p><p>Hostname requirements:</p><ul><li>Only letters, numbers, and hyphens are allowed</li><li>Must not start or end with a hyphen</li><li>Should not start with a number</li></ul><h3 id="info-tooltip" tabindex="-1">Info Tooltip <a class="header-anchor" href="#info-tooltip" aria-label="Permalink to &quot;Info Tooltip&quot;">​</a></h3><p>Clicking the &quot;i&quot; icon next to the input field displays the following help text:</p><blockquote><p>• Use a Fully Qualified Domain Name (FQDN) like <code>host.example.com</code><br> • Should be under 60 characters<br> • Never start with &quot;www&quot;<br> • Cannot begin with a digit or end with a hyphen &quot;-&quot;<br> • Use lowercase Latin letters only<br> • Each section must be unique (no repeating parts)</p><p>When choosing a hostname, avoid using any name that might later be needed for a user account or service subdomain (e.g., <code>bolt.example.com</code> or <code>adminbolt.example.com</code>).</p><p>Instead, pick something neutral and socially appropriate, as the hostname will appear in all outgoing mail headers.</p><p>Your hostname must be a Fully Qualified Domain Name (FQDN), meaning it should include both a name and a domain (like <code>host.example.com</code>) and contain at least two dots.</p><p>Choose wisely to avoid conflicts and ensure proper functionality.</p></blockquote><h3 id="change-hostname-button" tabindex="-1">Change Hostname Button <a class="header-anchor" href="#change-hostname-button" aria-label="Permalink to &quot;Change Hostname Button&quot;">​</a></h3><p>Clicking the <strong>Change Hostname</strong> button applies the new hostname.</p>',18)]))}const g=t(i,[["render",r]]);export{p as __pageData,g as default};
