import{_ as t,c as i,o,ag as n}from"./chunks/framework.Ds6Eueu6.js";const a="/screenshots/admin-customers.png",r="/screenshots/admin-customers-create.png",s="/screenshots/admin-customers-edit.png",l="/screenshots/admin-customers-delete.png",C=JSON.parse('{"title":"Customers","description":"","frontmatter":{},"headers":[{"level":3,"title":"Interface Elements","slug":"interface-elements","link":"#interface-elements","children":[]},{"level":3,"title":"Quick Actions","slug":"quick-actions","link":"#quick-actions","children":[]},{"level":3,"title":"Required Information","slug":"required-information","link":"#required-information","children":[]},{"level":3,"title":"Optional Information","slug":"optional-information","link":"#optional-information","children":[]},{"level":3,"title":"Creation Options","slug":"creation-options","link":"#creation-options","children":[]},{"level":3,"title":"Save Options","slug":"save-options","link":"#save-options","children":[]}],"relativePath":"user-guide/admin-panel/customers.md","filePath":"user-guide/admin-panel/customers.md"}'),c={name:"user-guide/admin-panel/customers.md"};function u(m,e,d,h,g,p){return o(),i("div",null,e[0]||(e[0]=[n('<h1 id="customers" tabindex="-1">Customers <a class="header-anchor" href="#customers" aria-label="Permalink to &quot;Customers&quot;">​</a></h1><p>The Customers section provides a centralized interface to create, view, edit, and manage customer accounts within your hosting environment.</p><h2 id="customers-overview" tabindex="-1">Customers Overview <a class="header-anchor" href="#customers-overview" aria-label="Permalink to &quot;Customers Overview&quot;">​</a></h2><p><strong>URL:</strong> <code>/admin/customers</code></p><p><img src="'+a+'" alt="Customers Interface"></p><h3 id="interface-elements" tabindex="-1">Interface Elements <a class="header-anchor" href="#interface-elements" aria-label="Permalink to &quot;Interface Elements&quot;">​</a></h3><h4 id="top-bar" tabindex="-1">Top Bar <a class="header-anchor" href="#top-bar" aria-label="Permalink to &quot;Top Bar&quot;">​</a></h4><ul><li><strong>New customer</strong> button: Create new customer</li><li><strong>Search</strong> field: Filter customers by name or email</li></ul><h4 id="list-view-columns" tabindex="-1">List View Columns <a class="header-anchor" href="#list-view-columns" aria-label="Permalink to &quot;List View Columns&quot;">​</a></h4><ul><li><strong>Customer Name</strong>: Full name of the account holder</li><li><strong>Email</strong>: Primary contact email address</li><li><strong>Owner</strong>: Account ownership assignment</li><li><strong>Actions</strong>: Available operations <ul><li>Edit Customer Details</li><li>Delete Customer Account</li></ul></li></ul><h4 id="list-controls" tabindex="-1">List Controls <a class="header-anchor" href="#list-controls" aria-label="Permalink to &quot;List Controls&quot;">​</a></h4><ul><li><strong>Per page</strong> dropdown: Adjust number of visible accounts</li><li><strong>Sort options</strong>: Order by customer name, email, or owner</li><li><strong>Pagination</strong>: Navigate through multiple pages</li></ul><h3 id="quick-actions" tabindex="-1">Quick Actions <a class="header-anchor" href="#quick-actions" aria-label="Permalink to &quot;Quick Actions&quot;">​</a></h3><p>For each customer, you can:</p><ul><li><strong>Edit</strong>: Modify customer information and settings</li><li><strong>Delete</strong>: Remove customer accounts from the system</li></ul><h2 id="creating-customer" tabindex="-1">Creating Customer <a class="header-anchor" href="#creating-customer" aria-label="Permalink to &quot;Creating Customer&quot;">​</a></h2><p><img src="'+r+'" alt="Create Customer Interface"></p><h3 id="required-information" tabindex="-1">Required Information <a class="header-anchor" href="#required-information" aria-label="Permalink to &quot;Required Information&quot;">​</a></h3><ol><li><p><strong>Name</strong></p><ul><li>Full name of the customer</li><li>Required field marked with asterisk</li></ul></li><li><p><strong>Login Credentials</strong></p><ul><li><strong>Username</strong> - Account login identifier (required)</li><li><strong>Password</strong> - Secure password for account access (required)</li></ul></li><li><p><strong>Contact Details</strong></p><ul><li><strong>Email</strong> - Primary email address (required)</li><li><strong>Phone</strong> - Contact phone number (optional)</li></ul></li></ol><h3 id="optional-information" tabindex="-1">Optional Information <a class="header-anchor" href="#optional-information" aria-label="Permalink to &quot;Optional Information&quot;">​</a></h3><ol><li><p><strong>Company Information</strong></p><ul><li>Company or organization name</li><li>Business details if applicable</li></ul></li><li><p><strong>Address Information</strong></p><ul><li>Expandable section for customer address details</li><li>Collapsible for streamlined interface with: <ul><li>Street address field with house icon</li><li>City and State fields arranged side-by-side</li><li>ZIP/Postal code and Country fields in two-column format</li></ul></li></ul></li></ol><h3 id="creation-options" tabindex="-1">Creation Options <a class="header-anchor" href="#creation-options" aria-label="Permalink to &quot;Creation Options&quot;">​</a></h3><ul><li><strong>Create</strong>: Set up single customer account</li><li><strong>Create &amp; create another</strong>: Create multiple customer accounts</li><li><strong>Cancel</strong>: Exit without creating</li></ul><h2 id="editing-customer-information" tabindex="-1">Editing Customer Information <a class="header-anchor" href="#editing-customer-information" aria-label="Permalink to &quot;Editing Customer Information&quot;">​</a></h2><p><img src="'+s+'" alt="Edit Customer Interface"></p><p>The edit interface provides the same fields as the creation form, allowing you to:</p><ul><li>Update contact information</li><li>Change login credentials</li><li>Modify company details</li><li>Adjust address information</li><li>Save changes or cancel modifications</li></ul><h2 id="edit-form-sections" tabindex="-1">Edit Form Sections <a class="header-anchor" href="#edit-form-sections" aria-label="Permalink to &quot;Edit Form Sections&quot;">​</a></h2><ul><li><strong>Customer Information</strong> - Name and basic details</li><li><strong>Contact Details</strong> - Email and phone information</li><li><strong>Company Information</strong> - Business-related data</li><li><strong>Address Information</strong> - Location and shipping details</li></ul><h3 id="save-options" tabindex="-1">Save Options <a class="header-anchor" href="#save-options" aria-label="Permalink to &quot;Save Options&quot;">​</a></h3><ul><li><strong>Save Changes</strong> - Apply modifications to the customer account</li><li><strong>Cancel</strong> - Discard changes and return to customer list</li></ul><h2 id="deleting-customers" tabindex="-1">Deleting Customers <a class="header-anchor" href="#deleting-customers" aria-label="Permalink to &quot;Deleting Customers&quot;">​</a></h2><p><img src="'+l+'" alt="Delete Customer Interface"></p><p>The deletion process includes:</p><ul><li>Confirmation dialog to prevent accidental deletion</li><li>Warning about permanent removal of customer data</li><li>Cancel option to abort the deletion process</li><li>Confirm button to proceed with removal</li></ul><h2 id="customer-management-benefits" tabindex="-1">Customer Management Benefits <a class="header-anchor" href="#customer-management-benefits" aria-label="Permalink to &quot;Customer Management Benefits&quot;">​</a></h2><p>The Customers Management section streamlines administration by providing:</p><ul><li>Centralized customer information storage</li><li>Quick access to account details</li><li>Simple creation and modification workflows</li><li>Secure customer deletion with confirmation</li><li>Efficient searching and filtering capabilities</li></ul>',38)]))}const b=t(c,[["render",u]]);export{C as __pageData,b as default};
