import{_ as o,c as r,o as t,a2 as a}from"./chunks/framework.B3hEhInr.js";const n="/screenshots/dns-records.png",i="/screenshots/dns-domain.png",s="/screenshots/create-dns-record.png",d="/screenshots/record-owner.png",l="/screenshots/dns-record-type.png",c="/screenshots/ttl-dropdown.png",h="/screenshots/enter-edit-mode.png",p="/screenshots/synced-at.png",m="/screenshots/resync-dns.png",T=JSON.parse('{"title":"DNS Records","description":"","frontmatter":{},"headers":[{"level":3,"title":"Fields:","slug":"fields","link":"#fields","children":[]}],"relativePath":"user-guide/admin-panel/dns-records.md","filePath":"user-guide/admin-panel/dns-records.md"}'),u={name:"user-guide/admin-panel/dns-records.md"};function g(b,e,f,_,y,S){return t(),r("div",null,e[0]||(e[0]=[a('<h1 id="dns-records" tabindex="-1">DNS Records <a class="header-anchor" href="#dns-records" aria-label="Permalink to &quot;DNS Records&quot;">​</a></h1><p>The <strong>DNS Records</strong> page allows administrators to manage DNS zone entries for hosted domains with full control and flexibility.</p><p><img src="'+n+'" alt="DNS Records Page"></p><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p><strong>URL:</strong> <code>/admin/dns-records</code></p><p>This interface provides:</p><ul><li>A domain selector to choose the DNS zone</li><li>A searchable and paginated table of existing DNS records</li><li>Options to create, edit, and manage records inline</li><li>A visual &quot;Synced&quot; indicator to track DNS propagation status</li><li>The ability to <strong>resynchronize DNS data</strong> with the server</li></ul><h2 id="domain-filter" tabindex="-1">Domain Filter <a class="header-anchor" href="#domain-filter" aria-label="Permalink to &quot;Domain Filter&quot;">​</a></h2><p>Use the <strong>Domain</strong> dropdown menu to filter records by specific domain name.</p><p><img src="'+i+'" alt="Domain Filter"></p><p>Click <strong>Reset</strong> to clear the filter.</p><h2 id="record-table-columns" tabindex="-1">Record Table Columns <a class="header-anchor" href="#record-table-columns" aria-label="Permalink to &quot;Record Table Columns&quot;">​</a></h2><p>Each record in the table displays:</p><ul><li><strong>Domain</strong> – The full domain name</li><li><strong>Name/Host</strong> – The subdomain or host (e.g. <code>www</code>, <code>mail</code>)</li><li><strong>Type</strong> – DNS record type (e.g. A, AAAA, MX, NS, etc.)</li><li><strong>Content</strong> – The value assigned to the record</li><li><strong>TTL</strong> – Time to Live, e.g. <code>1 hour</code>, <code>1 day</code></li><li><strong>Synced</strong> – Shows whether the record is synchronized</li><li><strong>Actions</strong> – Inline <strong>Edit</strong> and <strong>Delete</strong> options</li></ul><h2 id="adding-a-new-dns-record" tabindex="-1">Adding a New DNS Record <a class="header-anchor" href="#adding-a-new-dns-record" aria-label="Permalink to &quot;Adding a New DNS Record&quot;">​</a></h2><p>Click the <strong>&quot;Create New DNS Record&quot;</strong> button to open the record creation form.</p><p><img src="'+s+'" alt="Create DNS Record"></p><h3 id="fields" tabindex="-1">Fields: <a class="header-anchor" href="#fields" aria-label="Permalink to &quot;Fields:&quot;">​</a></h3><h4 id="_1-record-owner" tabindex="-1">1. Record Owner <a class="header-anchor" href="#_1-record-owner" aria-label="Permalink to &quot;1. Record Owner&quot;">​</a></h4><p>The fully qualified domain name or subdomain the record applies to.<br><img src="'+d+'" alt="Owner Field"></p><h4 id="_2-name" tabindex="-1">2. Name <a class="header-anchor" href="#_2-name" aria-label="Permalink to &quot;2. Name&quot;">​</a></h4><p>The hostname part of the record (e.g. <code>www</code>, <code>mail</code>, <code>@</code>). This value is optional depending on the record type.</p><h4 id="_3-type" tabindex="-1">3. Type <a class="header-anchor" href="#_3-type" aria-label="Permalink to &quot;3. Type&quot;">​</a></h4><p>Select the record type:<br><img src="'+l+'" alt="Record Type Dropdown"></p><p>Supported types:</p><ul><li>A</li><li>AAAA</li><li>CNAME</li><li>MX</li><li>TXT</li><li>PTR</li><li>SRV</li><li>NS</li><li>SOA</li></ul><h4 id="_4-content" tabindex="-1">4. Content <a class="header-anchor" href="#_4-content" aria-label="Permalink to &quot;4. Content&quot;">​</a></h4><p>The value of the record — can be an IP address, domain, or string depending on type.</p><h4 id="_5-ttl" tabindex="-1">5. TTL <a class="header-anchor" href="#_5-ttl" aria-label="Permalink to &quot;5. TTL&quot;">​</a></h4><p>Choose how long the record should be cached by DNS resolvers.<br><img src="'+c+'" alt="TTL Dropdown"></p><h2 id="editing-dns-records" tabindex="-1">Editing DNS Records <a class="header-anchor" href="#editing-dns-records" aria-label="Permalink to &quot;Editing DNS Records&quot;">​</a></h2><p>You can click <strong>&quot;Enter Edit Mode&quot;</strong> at the top-right or the <strong>Edit</strong> icon beside any record to modify it inline.</p><p><img src="'+h+'" alt="Inline Edit Mode"></p><h2 id="synced-column" tabindex="-1">Synced Column <a class="header-anchor" href="#synced-column" aria-label="Permalink to &quot;Synced Column&quot;">​</a></h2><p>The <strong>Synced</strong> column shows the current sync status:</p><ul><li>Synced (last sync: timestamp)</li><li>Not synced (last attempt: timestamp)</li></ul><p><img src="'+p+'" alt="Synced At Indicator"></p><h2 id="resync-dns-records" tabindex="-1">Resync DNS Records <a class="header-anchor" href="#resync-dns-records" aria-label="Permalink to &quot;Resync DNS Records&quot;">​</a></h2><p>Use the <strong>Resync DNS Records</strong> button to manually synchronize changes.</p><p><img src="'+m+'" alt="Resync Button"></p><p>This is useful after multiple edits to ensure the DNS server reflects the most recent changes.</p><h2 id="deleting-a-record" tabindex="-1">Deleting a Record <a class="header-anchor" href="#deleting-a-record" aria-label="Permalink to &quot;Deleting a Record&quot;">​</a></h2><p>Click the <strong>Delete</strong> icon to remove a record. A confirmation prompt will appear before deletion.</p><h2 id="additional-features" tabindex="-1">Additional Features <a class="header-anchor" href="#additional-features" aria-label="Permalink to &quot;Additional Features&quot;">​</a></h2><ul><li>Search bar for quick filtering by Domain, Name/Host, Type, and Content.</li><li>Paginate through large record sets with <code>Per page</code> dropdown</li></ul>',45)]))}const D=o(u,[["render",g]]);export{T as __pageData,D as default};
