import{_ as e,c as l,o as a,a2 as t}from"./chunks/framework.CHomSxuB.js";const o="/screenshots/jails.png",s="/screenshots/fail2ban-actions.png",f=JSON.parse('{"title":"Fail2Ban Settings","description":"","frontmatter":{},"headers":[{"level":3,"title":"1. Jails","slug":"_1-jails","link":"#_1-jails","children":[]},{"level":3,"title":"2. Actions","slug":"_2-actions","link":"#_2-actions","children":[]},{"level":3,"title":"Save Changes","slug":"save-changes","link":"#save-changes","children":[]}],"relativePath":"user-guide/admin-panel/fail2ban-settings.md","filePath":"user-guide/admin-panel/fail2ban-settings.md"}'),n={name:"user-guide/admin-panel/fail2ban-settings.md"};function r(d,i,c,u,g,h){return a(),l("div",null,i[0]||(i[0]=[t('<h1 id="fail2ban-settings" tabindex="-1">Fail2Ban Settings <a class="header-anchor" href="#fail2ban-settings" aria-label="Permalink to &quot;Fail2Ban Settings&quot;">​</a></h1><p>The <strong>Fail2Ban Settings</strong> section allows administrators to configure jails and actions for protecting services like SSH and FTP from unauthorized access and brute-force attacks.</p><p><strong>URL:</strong> <code>/admin/fail2ban/fail2-ban-settings</code></p><h2 id="tabs" tabindex="-1">Tabs <a class="header-anchor" href="#tabs" aria-label="Permalink to &quot;Tabs&quot;">​</a></h2><h3 id="_1-jails" tabindex="-1">1. Jails <a class="header-anchor" href="#_1-jails" aria-label="Permalink to &quot;1. Jails&quot;">​</a></h3><p><img src="'+o+'" alt="Fail2Ban Settings - Jails Tab"></p><h4 id="sshd-jail" tabindex="-1">SSHD Jail <a class="header-anchor" href="#sshd-jail" aria-label="Permalink to &quot;SSHD Jail&quot;">​</a></h4><ul><li><strong>SSHD Jail (Toggle)</strong> – Enables or disables the SSHD jail, which helps protect the SSH service from unauthorized access.</li><li><strong>Find Time</strong> – Time period in which a host must exceed the maximum number of failed login attempts to trigger a ban. <ul><li>Default: <code>1800 minute/s</code></li><li>Options: <code>seconds/s</code>, <code>minute/s</code>, <code>hour/s</code></li></ul></li><li><strong>Ban Time</strong> – Duration that an IP address will remain banned after detection. <ul><li>Default: <code>7200 minute/s</code></li><li>Options: <code>seconds/s</code>, <code>minute/s</code>, <code>hour/s</code></li></ul></li><li><strong>Ban Action</strong> – Method for blocking an IP address when a ban is triggered. <ul><li>Options: <ul><li><code>iptables</code></li><li><code>iptables-new</code></li><li><code>iptables-multiport</code></li><li><code>shorewall</code></li><li><code>firewalld-allports</code></li><li><code>firewalld-rich-rules</code></li><li><code>firewalld-ipset</code></li><li><code>firewalld-multiport</code></li></ul></li></ul></li><li><strong>Max Retry</strong> – Maximum number of failed login attempts allowed within the Find Time period. <ul><li>Default: <code>4</code></li></ul></li><li><strong>Log Path</strong> – Path to the log file Fail2Ban will use to monitor and detect intrusions. <ul><li>Default: <code>/var/log/fail2ban.log</code></li></ul></li></ul><h4 id="vsftpd-jail" tabindex="-1">vsFTPD Jail <a class="header-anchor" href="#vsftpd-jail" aria-label="Permalink to &quot;vsFTPD Jail&quot;">​</a></h4><ul><li><strong>vsFTPD Jail (Toggle)</strong> – Enables or disables the vsFTPD jail to protect the FTP server from unauthorized access.</li></ul><h3 id="_2-actions" tabindex="-1">2. Actions <a class="header-anchor" href="#_2-actions" aria-label="Permalink to &quot;2. Actions&quot;">​</a></h3><p><img src="'+s+'" alt="Fail2Ban Settings - Actions Tab"></p><h4 id="report-actions" tabindex="-1">Report Actions <a class="header-anchor" href="#report-actions" aria-label="Permalink to &quot;Report Actions&quot;">​</a></h4><ul><li><strong>Destination Email</strong> – Email address to which notifications will be sent. <ul><li>Default: <code>null</code></li></ul></li><li><strong>Sender</strong> – Sender email used for outgoing ban notifications. <ul><li>Default: <code>null</code></li></ul></li><li><strong>MTA</strong> – Mail transfer agent used to send ban notifications. <ul><li>Options: <ul><li><code>sendmail</code></li><li><code>mail</code></li></ul></li></ul></li><li><strong>Protocol</strong> – Communication protocol used for sending ban notifications. <ul><li>Options: <ul><li><code>tcp</code></li><li><code>udp</code></li></ul></li></ul></li><li><strong>Port</strong> – Port range to be banned. By default, it covers all ports from <code>0–65535</code>.</li><li><strong>Ban Action</strong> – Default action for banning (applies globally or per jail). <ul><li>Options: <ul><li><code>iptables</code></li><li><code>iptables-new</code></li><li><code>iptables-multiport</code></li><li><code>shorewall</code></li><li><code>firewalld-allports</code></li><li><code>firewalld-rich-rules</code></li><li><code>firewalld-ipset</code></li><li><code>firewalld-multiport</code></li></ul></li></ul></li></ul><h3 id="save-changes" tabindex="-1">Save Changes <a class="header-anchor" href="#save-changes" aria-label="Permalink to &quot;Save Changes&quot;">​</a></h3><p>Both tabs include a <strong>Save changes</strong> button to persist configuration updates.</p>',16)]))}const m=e(n,[["render",r]]);export{f as __pageData,m as default};
