import{_ as t,c as i,o as s,a2 as a}from"./chunks/framework.B3hEhInr.js";const l="/screenshots/whitelist.png",o="/screenshots/create-whitelist.png",r="/screenshots/edit-whitelist.png",f=JSON.parse('{"title":"Whitelist IPs","description":"","frontmatter":{},"headers":[{"level":3,"title":"Table Options","slug":"table-options","link":"#table-options","children":[]},{"level":3,"title":"Fields","slug":"fields","link":"#fields","children":[]},{"level":3,"title":"Fields","slug":"fields-1","link":"#fields-1","children":[]}],"relativePath":"user-guide/admin-panel/fail2ban-whitelist-ips.md","filePath":"user-guide/admin-panel/fail2ban-whitelist-ips.md"}'),n={name:"user-guide/admin-panel/fail2ban-whitelist-ips.md"};function d(h,e,p,c,g,u){return s(),i("div",null,e[0]||(e[0]=[a('<h1 id="whitelist-ips" tabindex="-1">Whitelist IPs <a class="header-anchor" href="#whitelist-ips" aria-label="Permalink to &quot;Whitelist IPs&quot;">​</a></h1><p>The <strong>Whitelist IPs</strong> page allows you to specify trusted IP addresses that should bypass Fail2Ban bans. This feature ensures uninterrupted access for key users or administrators.</p><p><img src="'+l+'" alt="Whitelist IPs"></p><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p><strong>URL:</strong> <code>/admin/fail2ban/fail2-ban-whitelisted-ips</code></p><p>This section shows a list of all currently whitelisted IP addresses with optional comments. You can add, edit, or remove any whitelisted IP from the interface.</p><ul><li><strong>Whitelisted IP</strong> – The IP address you wish to trust and exempt from Fail2Ban actions.</li><li><strong>Comment</strong> – A description or note explaining the purpose or owner of the IP (e.g. &quot;AdminBolt&quot;).</li></ul><h3 id="table-options" tabindex="-1">Table Options <a class="header-anchor" href="#table-options" aria-label="Permalink to &quot;Table Options&quot;">​</a></h3><ul><li><strong>Edit</strong> – Allows modifying the IP address or comment.</li><li><strong>Delete</strong> – Removes the entry from the whitelist.</li><li><strong>Pagination</strong> – Allows selecting how many entries to display per page: <strong>5, 10, 25, 50, All</strong>.</li></ul><h2 id="create-whitelist-ip" tabindex="-1">Create Whitelist IP <a class="header-anchor" href="#create-whitelist-ip" aria-label="Permalink to &quot;Create Whitelist IP&quot;">​</a></h2><p>To add a new trusted IP, click the <strong>Create Whitelist IP</strong> button.</p><p><img src="'+o+'" alt="Create Whitelist IP"></p><h3 id="fields" tabindex="-1">Fields <a class="header-anchor" href="#fields" aria-label="Permalink to &quot;Fields&quot;">​</a></h3><ul><li><strong>Whitelist IP</strong> <em>(required)</em> – The IPv4 or IPv6 address to be whitelisted.</li><li><strong>Add comment</strong> <em>(optional)</em> – Enter an explanatory note.</li></ul><p>Available actions:</p><ul><li><strong>Create</strong> – Saves the new whitelist entry.</li><li><strong>Create &amp; create another</strong> – Saves the entry and keeps the form open for the next one.</li><li><strong>Cancel</strong> – Closes the form without saving.</li></ul><h2 id="edit-whitelist-ip" tabindex="-1">Edit Whitelist IP <a class="header-anchor" href="#edit-whitelist-ip" aria-label="Permalink to &quot;Edit Whitelist IP&quot;">​</a></h2><p>Click the <strong>Edit</strong> icon next to an IP address to update it.</p><p><img src="'+r+'" alt="Edit Whitelist IP"></p><h3 id="fields-1" tabindex="-1">Fields <a class="header-anchor" href="#fields-1" aria-label="Permalink to &quot;Fields&quot;">​</a></h3><ul><li><strong>Whitelist IP</strong> – You can update the IP address here.</li><li><strong>Add comment</strong> – Update the description or tag related to this IP.</li></ul><p>After editing, click <strong>Save changes</strong> or <strong>Cancel</strong> to discard.</p>',22)]))}const P=t(n,[["render",d]]);export{f as __pageData,P as default};
