import{_ as e,c as a,o as t,a2 as l}from"./chunks/framework.CHomSxuB.js";const n="/screenshots/admin-fail2ban.png",o="/screenshots/admin-fail2ban-create.png",s="/screenshots/admin-fail2ban-settings.png",f=JSON.parse('{"title":"Fail2Ban","description":"","frontmatter":{},"headers":[{"level":3,"title":"Interface Elements","slug":"interface-elements","link":"#interface-elements","children":[]},{"level":3,"title":"Whitelist IP Configuration","slug":"whitelist-ip-configuration","link":"#whitelist-ip-configuration","children":[]},{"level":3,"title":"Action Buttons","slug":"action-buttons","link":"#action-buttons","children":[]},{"level":3,"title":"Settings Tabs","slug":"settings-tabs","link":"#settings-tabs","children":[]},{"level":3,"title":"Navigation Hierarchy","slug":"navigation-hierarchy","link":"#navigation-hierarchy","children":[]},{"level":3,"title":"Settings Management","slug":"settings-management","link":"#settings-management","children":[]},{"level":3,"title":"Configuration Management","slug":"configuration-management","link":"#configuration-management","children":[]},{"level":3,"title":"Security Considerations","slug":"security-considerations","link":"#security-considerations","children":[]},{"level":3,"title":"Email Configuration","slug":"email-configuration","link":"#email-configuration","children":[]},{"level":3,"title":"Common Issues","slug":"common-issues","link":"#common-issues","children":[]},{"level":3,"title":"Resolution Steps","slug":"resolution-steps","link":"#resolution-steps","children":[]},{"level":3,"title":"Related Security Features","slug":"related-security-features","link":"#related-security-features","children":[]},{"level":3,"title":"System Dependencies","slug":"system-dependencies","link":"#system-dependencies","children":[]},{"level":3,"title":"Regular Tasks","slug":"regular-tasks","link":"#regular-tasks","children":[]},{"level":3,"title":"Reporting","slug":"reporting","link":"#reporting","children":[]},{"level":3,"title":"Prerequisites","slug":"prerequisites","link":"#prerequisites","children":[]},{"level":3,"title":"Technical Specifications","slug":"technical-specifications","link":"#technical-specifications","children":[]}],"relativePath":"user-guide/admin-panel/fail2ban.md","filePath":"user-guide/admin-panel/fail2ban.md"}'),r={name:"user-guide/admin-panel/fail2ban.md"};function c(u,i,h,d,g,m){return t(),a("div",null,i[0]||(i[0]=[l('<h1 id="fail2ban" tabindex="-1">Fail2Ban <a class="header-anchor" href="#fail2ban" aria-label="Permalink to &quot;Fail2Ban&quot;">​</a></h1><p>Fail2Ban provides automated protection against brute-force attacks by monitoring system logs and temporarily banning IP addresses that show malicious behavior.</p><h2 id="whitelist-ips-overview" tabindex="-1">Whitelist IPs Overview <a class="header-anchor" href="#whitelist-ips-overview" aria-label="Permalink to &quot;Whitelist IPs Overview&quot;">​</a></h2><p><strong>URL:</strong> <code>/admin/fail2ban/whitelist-ips</code><img src="'+n+'" alt="Fail2Ban Whitelist Interface"></p><h3 id="interface-elements" tabindex="-1">Interface Elements <a class="header-anchor" href="#interface-elements" aria-label="Permalink to &quot;Interface Elements&quot;">​</a></h3><h4 id="navigation-bar" tabindex="-1">Navigation Bar <a class="header-anchor" href="#navigation-bar" aria-label="Permalink to &quot;Navigation Bar&quot;">​</a></h4><ul><li><strong>Fail2Ban</strong>: Main security section</li><li><strong>Whitelist IPs</strong>: Currently selected</li><li><strong>List</strong> view: Active view</li></ul><h4 id="top-bar" tabindex="-1">Top Bar <a class="header-anchor" href="#top-bar" aria-label="Permalink to &quot;Top Bar&quot;">​</a></h4><ul><li><strong>New Whitelist IP</strong> button: Add IP addresses to whitelist</li></ul><h4 id="management-tabs" tabindex="-1">Management Tabs <a class="header-anchor" href="#management-tabs" aria-label="Permalink to &quot;Management Tabs&quot;">​</a></h4><ul><li><strong>Whitelist IPs</strong>: Configure trusted IP addresses (current tab)</li><li><strong>Banned IPs</strong>: View and manage banned addresses</li><li><strong>Fail2Ban Settings</strong>: Configure global settings</li></ul><h4 id="empty-state" tabindex="-1">Empty State <a class="header-anchor" href="#empty-state" aria-label="Permalink to &quot;Empty State&quot;">​</a></h4><ul><li>Displays &quot;No Whitelist IPs&quot; when no entries exist</li><li>Clean interface for immediate action</li></ul><h2 id="creating-whitelist-ip" tabindex="-1">Creating Whitelist IP <a class="header-anchor" href="#creating-whitelist-ip" aria-label="Permalink to &quot;Creating Whitelist IP&quot;">​</a></h2><p><strong>URL:</strong> <code>/admin/fail2ban/whitelist-ips/create</code><img src="'+o+'" alt="Create Whitelist IP Interface"></p><h3 id="whitelist-ip-configuration" tabindex="-1">Whitelist IP Configuration <a class="header-anchor" href="#whitelist-ip-configuration" aria-label="Permalink to &quot;Whitelist IP Configuration&quot;">​</a></h3><ol><li><p><strong>Whitelist IP Field</strong></p><ul><li>Enter IP address to whitelist</li><li>Supports single IP addresses</li><li>Required field for creation</li></ul></li><li><p><strong>Add Comment Section</strong></p><ul><li>Optional description field</li><li>Document purpose or reason</li><li>Helps maintain organization</li></ul></li></ol><h3 id="action-buttons" tabindex="-1">Action Buttons <a class="header-anchor" href="#action-buttons" aria-label="Permalink to &quot;Action Buttons&quot;">​</a></h3><ul><li><strong>Create</strong>: Save whitelist entry</li><li><strong>Create &amp; create another</strong>: Quick successive entries</li><li><strong>Cancel</strong>: Abort operation</li></ul><h2 id="fail2ban-settings" tabindex="-1">Fail2Ban Settings <a class="header-anchor" href="#fail2ban-settings" aria-label="Permalink to &quot;Fail2Ban Settings&quot;">​</a></h2><p><strong>URL:</strong> <code>/admin/fail2ban/settings</code><img src="'+s+'" alt="Fail2Ban Settings Interface"></p><h3 id="settings-tabs" tabindex="-1">Settings Tabs <a class="header-anchor" href="#settings-tabs" aria-label="Permalink to &quot;Settings Tabs&quot;">​</a></h3><h4 id="general-tab" tabindex="-1">General Tab <a class="header-anchor" href="#general-tab" aria-label="Permalink to &quot;General Tab&quot;">​</a></h4><h5 id="enable-jails" tabindex="-1">Enable Jails <a class="header-anchor" href="#enable-jails" aria-label="Permalink to &quot;Enable Jails&quot;">​</a></h5><ul><li>Toggle to activate all jails globally</li><li>Description: &quot;Enabling this option will activate all jails.&quot;</li></ul><h5 id="save-changes-button" tabindex="-1">Save Changes Button <a class="header-anchor" href="#save-changes-button" aria-label="Permalink to &quot;Save Changes Button&quot;">​</a></h5><ul><li>Applies all modifications</li></ul><h4 id="actions-tab" tabindex="-1">Actions Tab <a class="header-anchor" href="#actions-tab" aria-label="Permalink to &quot;Actions Tab&quot;">​</a></h4><h5 id="report-actions" tabindex="-1">Report Actions <a class="header-anchor" href="#report-actions" aria-label="Permalink to &quot;Report Actions&quot;">​</a></h5><ol><li><p><strong>Destination Email</strong></p><ul><li>Email address for notifications</li><li>Format: Default null</li><li>Description: &quot;This is the email address where notifications will be sent based on the configurations in the jail (conf.local.d/*) files.&quot;</li></ul></li><li><p><strong>Sender</strong></p><ul><li>Email sender configuration</li><li>Format: Default null</li><li>Description: &quot;The sender email address is used specifically for certain actions in the configuration files to define who the notifications come from.&quot;</li></ul></li><li><p><strong>MTA (Mail Transfer Agent)</strong></p><ul><li>Dropdown selection</li><li>Options: Select mail system</li><li>Description: &quot;Specify the email sending method for Fail2Ban: use sendmail for the default option, or switch to mail to use a traditional email system.&quot;</li></ul></li><li><p><strong>Protocol</strong></p><ul><li>Communication protocol selection</li><li>Options: Various protocols available</li><li>Description: &quot;Specify the communication protocol used by Fail2Ban for its actions: by default, it uses TCP for reliable data transmission.&quot;</li></ul></li><li><p><strong>Port</strong></p><ul><li>Network port configuration</li><li>Default: 0-65535</li><li>Description: &quot;Specify the range of ports to be banned, which is generally set individually for each specific jail. By default, it covers all ports from 0 to 65535.&quot;</li></ul></li><li><p><strong>Ban Action</strong></p><ul><li>Default action configuration</li><li>Dropdown selection</li><li>Description: &quot;Sets the default action for banning, which can be customized globally or for individual sections in the jail.local file.&quot;</li></ul></li></ol><h5 id="save-changes-button-1" tabindex="-1">Save Changes Button <a class="header-anchor" href="#save-changes-button-1" aria-label="Permalink to &quot;Save Changes Button&quot;">​</a></h5><ul><li>Applies all action configurations</li></ul><h4 id="jails-tab" tabindex="-1">Jails Tab <a class="header-anchor" href="#jails-tab" aria-label="Permalink to &quot;Jails Tab&quot;">​</a></h4><h5 id="available-jails" tabindex="-1">Available Jails <a class="header-anchor" href="#available-jails" aria-label="Permalink to &quot;Available Jails&quot;">​</a></h5><ol><li><p><strong>SSHD Jail</strong></p><ul><li>Toggle for SSH protection</li><li>Description: &quot;Turning on this option will activate the SSHD jail, which helps protect your SSH service from unauthorized access.&quot;</li></ul></li><li><p><strong>Apache Jail</strong></p><ul><li>Toggle for Apache protection</li><li>Description: &quot;Turning on this option will activate the Apache jail, which helps protect your Apache server from unauthorized access and malicious activity.&quot;</li></ul></li><li><p><strong>vsFTPD Jail</strong></p><ul><li>Toggle for FTP protection</li><li>Description: &quot;Turning on this option will activate the vsFTPD jail, which helps protect your vFTPD server from unauthorized access and malicious activity.&quot;</li></ul></li></ol><h5 id="save-changes-button-2" tabindex="-1">Save Changes Button <a class="header-anchor" href="#save-changes-button-2" aria-label="Permalink to &quot;Save Changes Button&quot;">​</a></h5><ul><li>Applies all jail configurations</li></ul><h2 id="feature-structure" tabindex="-1">Feature Structure <a class="header-anchor" href="#feature-structure" aria-label="Permalink to &quot;Feature Structure&quot;">​</a></h2><h3 id="navigation-hierarchy" tabindex="-1">Navigation Hierarchy <a class="header-anchor" href="#navigation-hierarchy" aria-label="Permalink to &quot;Navigation Hierarchy&quot;">​</a></h3><ul><li>Security → Fail2Ban → Whitelist IPs/Banned IPs/Settings</li><li>Logical organization for security features</li><li>Easy access from main navigation</li></ul><h3 id="settings-management" tabindex="-1">Settings Management <a class="header-anchor" href="#settings-management" aria-label="Permalink to &quot;Settings Management&quot;">​</a></h3><ol><li><p><strong>General Settings</strong></p><ul><li>Global jail activation</li><li>System-wide configurations</li></ul></li><li><p><strong>Action Configuration</strong></p><ul><li>Email notification setup</li><li>Protocol specifications</li><li>Ban action parameters</li></ul></li><li><p><strong>Jail Management</strong></p><ul><li>Service-specific protection</li><li>Independent toggle controls</li><li>Customizable security layers</li></ul></li></ol><h2 id="best-practices" tabindex="-1">Best Practices <a class="header-anchor" href="#best-practices" aria-label="Permalink to &quot;Best Practices&quot;">​</a></h2><h3 id="configuration-management" tabindex="-1">Configuration Management <a class="header-anchor" href="#configuration-management" aria-label="Permalink to &quot;Configuration Management&quot;">​</a></h3><ul><li>Start with default settings</li><li>Enable jails for active services</li><li>Configure email notifications</li><li>Test configurations thoroughly</li><li>Document all changes</li></ul><h3 id="security-considerations" tabindex="-1">Security Considerations <a class="header-anchor" href="#security-considerations" aria-label="Permalink to &quot;Security Considerations&quot;">​</a></h3><ul><li>Monitor ban patterns</li><li>Adjust thresholds based on attacks</li><li>Keep whitelist minimal</li><li>Use comments for accountability</li><li>Regular security audits</li></ul><h3 id="email-configuration" tabindex="-1">Email Configuration <a class="header-anchor" href="#email-configuration" aria-label="Permalink to &quot;Email Configuration&quot;">​</a></h3><ul><li>Use valid destination email</li><li>Configure proper sender address</li><li>Select appropriate MTA</li><li>Test notification delivery</li><li>Monitor email alerts</li></ul><h2 id="troubleshooting" tabindex="-1">Troubleshooting <a class="header-anchor" href="#troubleshooting" aria-label="Permalink to &quot;Troubleshooting&quot;">​</a></h2><h3 id="common-issues" tabindex="-1">Common Issues <a class="header-anchor" href="#common-issues" aria-label="Permalink to &quot;Common Issues&quot;">​</a></h3><ul><li>Email notifications not working</li><li>Jails not activating properly</li><li>Configuration conflicts</li><li>Port specification errors</li></ul><h3 id="resolution-steps" tabindex="-1">Resolution Steps <a class="header-anchor" href="#resolution-steps" aria-label="Permalink to &quot;Resolution Steps&quot;">​</a></h3><ol><li>Verify email settings</li><li>Check MTA configuration</li><li>Confirm jail status</li><li>Review system logs</li><li>Restart Fail2Ban service</li><li>Contact support if needed</li></ol><h2 id="integration-points" tabindex="-1">Integration Points <a class="header-anchor" href="#integration-points" aria-label="Permalink to &quot;Integration Points&quot;">​</a></h2><h3 id="related-security-features" tabindex="-1">Related Security Features <a class="header-anchor" href="#related-security-features" aria-label="Permalink to &quot;Related Security Features&quot;">​</a></h3><ul><li>Firewall Rules</li><li>Security Scan</li><li>API Keys</li><li>Linux Web Users</li></ul><h3 id="system-dependencies" tabindex="-1">System Dependencies <a class="header-anchor" href="#system-dependencies" aria-label="Permalink to &quot;System Dependencies&quot;">​</a></h3><ul><li>Works alongside firewall</li><li>Integrates with system logs</li><li>Requires mail server for notifications</li><li>Service-specific log monitoring</li></ul><h2 id="monitoring-and-maintenance" tabindex="-1">Monitoring and Maintenance <a class="header-anchor" href="#monitoring-and-maintenance" aria-label="Permalink to &quot;Monitoring and Maintenance&quot;">​</a></h2><h3 id="regular-tasks" tabindex="-1">Regular Tasks <a class="header-anchor" href="#regular-tasks" aria-label="Permalink to &quot;Regular Tasks&quot;">​</a></h3><ul><li>Review active jails</li><li>Check notification delivery</li><li>Monitor false positives</li><li>Update configurations</li><li>Verify service protection</li></ul><h3 id="reporting" tabindex="-1">Reporting <a class="header-anchor" href="#reporting" aria-label="Permalink to &quot;Reporting&quot;">​</a></h3><ul><li>Track ban statistics</li><li>Analyze attack patterns</li><li>Document security incidents</li><li>Generate compliance reports</li></ul><h2 id="system-requirements" tabindex="-1">System Requirements <a class="header-anchor" href="#system-requirements" aria-label="Permalink to &quot;System Requirements&quot;">​</a></h2><h3 id="prerequisites" tabindex="-1">Prerequisites <a class="header-anchor" href="#prerequisites" aria-label="Permalink to &quot;Prerequisites&quot;">​</a></h3><ul><li>Active AdminBolt subscription</li><li>Security module enabled</li><li>Mail server configured</li><li>Proper permissions</li><li>System log access</li></ul><h3 id="technical-specifications" tabindex="-1">Technical Specifications <a class="header-anchor" href="#technical-specifications" aria-label="Permalink to &quot;Technical Specifications&quot;">​</a></h3><ul><li>Supports multiple services</li><li>Email notification system</li><li>Port range configuration</li><li>Protocol selection options</li><li>Real-time protection</li></ul><h2 id="management-benefits" tabindex="-1">Management Benefits <a class="header-anchor" href="#management-benefits" aria-label="Permalink to &quot;Management Benefits&quot;">​</a></h2><ul><li>Centralized security management</li><li>Automated threat response</li><li>Service-specific protection</li><li>Flexible ban configurations</li><li>Email notification system</li><li>Whitelist and blacklist control</li><li>Multi-service jail system</li><li>Real-time log monitoring</li></ul>',71)]))}const b=e(r,[["render",c]]);export{f as __pageData,b as default};
