import{_ as t,c as r,o as l,ag as a}from"./chunks/framework.Ds6Eueu6.js";const o="/screenshots/firewall-rules.png",s="/screenshots/create-rule.png",m=JSON.parse('{"title":"Firewall Rules","description":"","frontmatter":{},"headers":[{"level":3,"title":"Features","slug":"features","link":"#features","children":[]},{"level":3,"title":"Table Columns","slug":"table-columns","link":"#table-columns","children":[]},{"level":3,"title":"Search","slug":"search","link":"#search","children":[]},{"level":3,"title":"Pagination","slug":"pagination","link":"#pagination","children":[]},{"level":3,"title":"Fields","slug":"fields","link":"#fields","children":[]},{"level":3,"title":"Buttons","slug":"buttons","link":"#buttons","children":[]}],"relativePath":"user-guide/admin-panel/firewall-rules.md","filePath":"user-guide/admin-panel/firewall-rules.md"}'),i={name:"user-guide/admin-panel/firewall-rules.md"};function n(d,e,c,u,h,g){return l(),r("div",null,e[0]||(e[0]=[a('<h1 id="firewall-rules" tabindex="-1">Firewall Rules <a class="header-anchor" href="#firewall-rules" aria-label="Permalink to &quot;Firewall Rules&quot;">​</a></h1><p>The <strong>Firewall Rules</strong> page allows administrators to manage system-level firewall policies, including port access, IP restrictions, and service protection.</p><p><img src="'+o+'" alt="Firewall Rules"></p><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p><strong>URL:</strong> <code>/admin/firewall-rules</code></p><p>This section presents a table of current firewall rules, segmented by action, IP source/destination, port access, and network zone.</p><p>Admins can view existing rules, create new ones, or remove rules using the interface tools.</p><h3 id="features" tabindex="-1">Features <a class="header-anchor" href="#features" aria-label="Permalink to &quot;Features&quot;">​</a></h3><ul><li>Button to automatically fix critical system ports: <code>Fix System Ports</code></li><li>Button to create custom rules: <code>+ Create Rule</code></li></ul><h3 id="table-columns" tabindex="-1">Table Columns <a class="header-anchor" href="#table-columns" aria-label="Permalink to &quot;Table Columns&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Column</th><th>Description</th></tr></thead><tbody><tr><td><strong>Action</strong></td><td>The rule type, either <code>ALLOW</code> or <code>DENY</code>.</td></tr><tr><td><strong>From IP</strong></td><td>Source IP address (e.g. <code>Anywhere</code>).</td></tr><tr><td><strong>To Port</strong></td><td>Destination port with protocol (e.g. <code>22/tcp</code>).</td></tr><tr><td><strong>To IP</strong></td><td>Destination IP address.</td></tr><tr><td><strong>Zone</strong></td><td>The network zone (usually <code>public</code>).</td></tr><tr><td><strong>Delete</strong></td><td>Deletes the rule entry.</td></tr></tbody></table><h3 id="search" tabindex="-1">Search <a class="header-anchor" href="#search" aria-label="Permalink to &quot;Search&quot;">​</a></h3><p>You can filter/search rules using the search bar above the table.<br> Search works across the following fields:</p><ul><li><strong>Action</strong></li><li><strong>From IP</strong></li><li><strong>To Port</strong></li><li><strong>To IP</strong></li><li><strong>Zone</strong></li></ul><h3 id="pagination" tabindex="-1">Pagination <a class="header-anchor" href="#pagination" aria-label="Permalink to &quot;Pagination&quot;">​</a></h3><p>Users can adjust the number of rules displayed per page:</p><ul><li><code>5</code>, <code>10</code>, <code>25</code>, <code>50</code>, or <code>All</code></li></ul><h2 id="create-rule" tabindex="-1">Create Rule <a class="header-anchor" href="#create-rule" aria-label="Permalink to &quot;Create Rule&quot;">​</a></h2><p>Clicking the <code>+ Create Rule</code> button opens a modal to add a new firewall rule.</p><p><img src="'+s+'" alt="Create Rule Modal"></p><h3 id="fields" tabindex="-1">Fields <a class="header-anchor" href="#fields" aria-label="Permalink to &quot;Fields&quot;">​</a></h3><ul><li><p><strong>Action</strong><br> Dropdown field to select <code>ALLOW</code> or <code>DENY</code>.</p></li><li><p><strong>Port or IP</strong><br> Input field to define the port (e.g. <code>443/tcp</code>) or IP address to which the rule applies.</p></li></ul><h3 id="buttons" tabindex="-1">Buttons <a class="header-anchor" href="#buttons" aria-label="Permalink to &quot;Buttons&quot;">​</a></h3><ul><li><strong>Submit</strong> — saves and applies the new rule.</li><li><strong>Cancel</strong> — closes the modal without saving.</li></ul>',24)]))}const b=t(i,[["render",n]]);export{m as __pageData,b as default};
