import{_ as t,c as a,o as s,a2 as l}from"./chunks/framework.CHomSxuB.js";const i="/screenshots/health-check.png",r="/screenshots/health-install.png",o="/screenshots/health-statuslog.png",k=JSON.parse('{"title":"Health Check","description":"","frontmatter":{},"headers":[{"level":3,"title":"Install/Reinstall","slug":"install-reinstall","link":"#install-reinstall","children":[]},{"level":3,"title":"Restart","slug":"restart","link":"#restart","children":[]},{"level":3,"title":"Status Log","slug":"status-log","link":"#status-log","children":[]}],"relativePath":"user-guide/admin-panel/health-check.md","filePath":"user-guide/admin-panel/health-check.md"}'),n={name:"user-guide/admin-panel/health-check.md"};function c(h,e,u,g,d,p){return s(),a("div",null,e[0]||(e[0]=[l('<h1 id="health-check" tabindex="-1">Health Check <a class="header-anchor" href="#health-check" aria-label="Permalink to &quot;Health Check&quot;">​</a></h1><p>The <strong>Health Check</strong> page provides a real-time overview of installed system services, their status, and quick access to service actions such as installation, restarting, or checking logs.</p><p><img src="'+i+'" alt="Health Check Interface"></p><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p><strong>URL:</strong> <code>/admin/health-check</code></p><p>This page allows administrators to:</p><ul><li>Monitor the <strong>status</strong> of key services (e.g., MySQL, Dovecot, Fail2Ban, etc.).</li><li>Check if a service is currently <strong>Installed</strong> and/or <strong>Running</strong>.</li><li>Perform quick actions: <ul><li><strong>Install/Reinstall</strong> – (Re)install the selected service.</li><li><strong>Restart</strong> – Restart the service if it is currently running.</li><li><strong>Status Log</strong> – View the current service log output.</li></ul></li></ul><p>Each service is grouped into categories:</p><ul><li><strong>Database</strong> – MySQL, PHPMyAdmin</li><li><strong>Mail</strong> – Dovecot, Postfix, Roundcube, Rspamd</li><li><strong>Network</strong> – Vsftpd, PowerDNS</li><li><strong>Other</strong> – Fail2Ban, Firewall, Git, Supervisor, MyApache, File Manager, etc.</li></ul><h2 id="actions" tabindex="-1">Actions <a class="header-anchor" href="#actions" aria-label="Permalink to &quot;Actions&quot;">​</a></h2><h3 id="install-reinstall" tabindex="-1">Install/Reinstall <a class="header-anchor" href="#install-reinstall" aria-label="Permalink to &quot;Install/Reinstall&quot;">​</a></h3><p>Clicking <strong>Install/Reinstall</strong> will initiate the installation process for the selected service. Once successful, a notification appears:</p><blockquote><p><code>Success: [ServiceName]Installer actions executed successfully!</code></p></blockquote><p><img src="'+r+'" alt="Install Modal"></p><h3 id="restart" tabindex="-1">Restart <a class="header-anchor" href="#restart" aria-label="Permalink to &quot;Restart&quot;">​</a></h3><p>Clicking <strong>Restart</strong> will send a restart signal to the service. If successful, the following message is shown:</p><blockquote><p><code>Success: Service restarted successfully!</code></p></blockquote><p>No modal is shown — only a toast notification.</p><h3 id="status-log" tabindex="-1">Status Log <a class="header-anchor" href="#status-log" aria-label="Permalink to &quot;Status Log&quot;">​</a></h3><p>Clicking <strong>Status Log</strong> opens a modal displaying logs for the service.</p><p>Example:</p><p><img src="'+o+'" alt="Status Log"></p><h2 id="search-functionality" tabindex="-1">Search Functionality <a class="header-anchor" href="#search-functionality" aria-label="Permalink to &quot;Search Functionality&quot;">​</a></h2><p>The search bar filters services <strong>based on the Name column</strong> only.</p>',24)]))}const f=t(n,[["render",c]]);export{k as __pageData,f as default};
