import{_ as e,c as n,o as s,a2 as a}from"./chunks/framework.B3hEhInr.js";const i="/screenshots/hosting-accounts.png",o="/screenshots/create-hosting-account.png",r="/screenshots/impersonate-user.png",l="/screenshots/hosting-usage.png",g="/screenshots/edit-hosting-account.png",d="/screenshots/change-pass.png",A=JSON.parse('{"title":"Accounts","description":"","frontmatter":{},"headers":[{"level":3,"title":"Actions","slug":"actions","link":"#actions","children":[]},{"level":3,"title":"Hosting Configuration","slug":"hosting-configuration","link":"#hosting-configuration","children":[]},{"level":3,"title":"Reseller Details","slug":"reseller-details","link":"#reseller-details","children":[]}],"relativePath":"user-guide/admin-panel/hosting-accounts.md","filePath":"user-guide/admin-panel/hosting-accounts.md"}'),c={name:"user-guide/admin-panel/hosting-accounts.md"};function u(h,t,p,m,b,f){return s(),n("div",null,t[0]||(t[0]=[a('<h1 id="accounts" tabindex="-1">Accounts <a class="header-anchor" href="#accounts" aria-label="Permalink to &quot;Accounts&quot;">​</a></h1><p>The <strong>Hosting Accounts</strong> section allows administrators to create, manage, and monitor individual hosting accounts under a specific hosting plan.</p><p><img src="'+i+'" alt="Hosting Accounts Page"></p><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p><strong>URL:</strong> <code>/admin/hosting-accounts</code></p><p>This page displays all created hosting accounts, along with the following columns:</p><ul><li><strong>Main Domain</strong> – Primary domain assigned to the account</li><li><strong>Username</strong> – System username of the hosting account</li><li><strong>Hosting Plan</strong> – Assigned hosting plan (e.g. Default)</li><li><strong>Reseller</strong> – Associated reseller (if any)</li><li><strong>Status</strong> – Account status (Active, Suspended)</li></ul><h3 id="actions" tabindex="-1">Actions <a class="header-anchor" href="#actions" aria-label="Permalink to &quot;Actions&quot;">​</a></h3><ul><li><strong>Login</strong> – Allows administrators to impersonate the user (impersonation mode)</li><li><strong>More options</strong> (⋮): <ul><li><strong>Usage Details</strong> – View live usage data for disk, bandwidth, databases, email, etc.</li><li><strong>Edit Hosting Account</strong> – Change hosting plan, reseller, or SSH access</li><li><strong>Open Website</strong> / <strong>Open Website (local)</strong> – Visit hosted domain</li><li><strong>Change Password</strong> – Update the account password</li><li><strong>Suspend</strong> – Temporarily disable account access</li><li><strong>Delete</strong> – Permanently delete the account</li></ul></li></ul><h2 id="search-and-filtering" tabindex="-1">Search and Filtering <a class="header-anchor" href="#search-and-filtering" aria-label="Permalink to &quot;Search and Filtering&quot;">​</a></h2><ul><li><p><strong>Search</strong> – Filters records by the following fields:</p><ul><li>Main Domain</li><li>Username</li><li>Hosting Plan</li><li>Reseller</li><li>Status</li></ul></li><li><p><strong>Filter</strong> – Located next to the search bar:</p><ul><li>Filter by <strong>Hosting Plan</strong></li><li>Filter by <strong>Reseller</strong></li><li>Includes a <strong>Reset</strong> button to clear all filters</li></ul></li></ul><h2 id="creating-a-hosting-account" tabindex="-1">Creating a Hosting Account <a class="header-anchor" href="#creating-a-hosting-account" aria-label="Permalink to &quot;Creating a Hosting Account&quot;">​</a></h2><p>Clicking the <strong>Create Hosting Account</strong> button opens the configuration modal:</p><p><img src="'+o+'" alt="Create Hosting Account"></p><h3 id="hosting-configuration" tabindex="-1">Hosting Configuration <a class="header-anchor" href="#hosting-configuration" aria-label="Permalink to &quot;Hosting Configuration&quot;">​</a></h3><ul><li><strong>Domain</strong> <em>(required)</em> – Main domain name for the account</li><li><strong>Hosting Plan</strong> <em>(required)</em> – Select one of the available hosting plans</li><li><strong>Username</strong> <em>(required)</em> – System username</li><li><strong>Password</strong> <em>(required)</em> – Secure password input <ul><li>Password strength is displayed</li><li>You can click “Generate Secure Credentials” for an auto-generated strong password</li></ul></li><li><strong>Enable SSH Access</strong> – Optional toggle to enable shell access</li></ul><h3 id="reseller-details" tabindex="-1">Reseller Details <a class="header-anchor" href="#reseller-details" aria-label="Permalink to &quot;Reseller Details&quot;">​</a></h3><ul><li><strong>Reseller</strong> – Assign the account to a reseller (optional)</li></ul><p>Click <strong>Create</strong> to finalize the hosting account.</p><h2 id="impersonating-a-user" tabindex="-1">Impersonating a User <a class="header-anchor" href="#impersonating-a-user" aria-label="Permalink to &quot;Impersonating a User&quot;">​</a></h2><p>Clicking the <strong>Login</strong> button next to a hosting account will impersonate that user and open their dashboard.</p><p><img src="'+r+'" alt="Impersonate User"></p><p>While impersonating:</p><ul><li>A blue bar appears at the top of the screen indicating <strong>&quot;Impersonating user {username}&quot;</strong></li><li>You will see the user&#39;s interface exactly as they would</li><li>To exit impersonation mode, click the <strong>Leave</strong> button in the top bar</li></ul><h2 id="viewing-hosting-account-usage" tabindex="-1">Viewing Hosting Account Usage <a class="header-anchor" href="#viewing-hosting-account-usage" aria-label="Permalink to &quot;Viewing Hosting Account Usage&quot;">​</a></h2><p>You can view detailed usage per account:</p><p><img src="'+l+'" alt="Usage Details"></p><p>This dialog displays current usage vs. limits for various hosting resources, including:</p><table tabindex="0"><thead><tr><th>Resource</th><th>Used</th><th>Total</th><th>Usage</th></tr></thead><tbody><tr><td>Disk Space</td><td>e.g. 0.14 MB</td><td>e.g. 10000 MB</td><td>e.g. 0%</td></tr><tr><td>Database Disk Space</td><td>e.g. 0.00 MB</td><td>e.g. 9999.86 MB</td><td>e.g. N/A</td></tr><tr><td>Bandwidth</td><td>e.g. 5.57 MB</td><td>e.g. 50000 MB</td><td>e.g. 0%</td></tr><tr><td>Addon Domains</td><td>e.g. 2</td><td>e.g. 5</td><td>e.g. 40%</td></tr><tr><td>Subdomains</td><td>e.g. 1</td><td>e.g. 5</td><td>e.g. 20%</td></tr><tr><td>Parked Domains</td><td>e.g. 0</td><td>e.g. 5</td><td>e.g. 0%</td></tr><tr><td>FTP Accounts</td><td>e.g. 3</td><td>e.g. 5</td><td>e.g. 60%</td></tr><tr><td>Databases</td><td>e.g. 2</td><td>e.g. 5</td><td>e.g. 40%</td></tr><tr><td>Email Accounts</td><td>e.g. 4</td><td>e.g. 5</td><td>e.g. 80%</td></tr><tr><td>Email Forwarders</td><td>e.g. 1</td><td>e.g. 5</td><td>e.g. 20%</td></tr><tr><td>Email Autoresponders</td><td>e.g. 0</td><td>e.g. 5</td><td>e.g. 0%</td></tr></tbody></table><blockquote><p><strong>Note:</strong> Values shown above are examples. The actual usage will depend on each individual account&#39;s activity and plan limits.</p></blockquote><p>You can click <strong>Refresh Data</strong> to update usage stats.</p><h2 id="editing-a-hosting-account" tabindex="-1">Editing a Hosting Account <a class="header-anchor" href="#editing-a-hosting-account" aria-label="Permalink to &quot;Editing a Hosting Account&quot;">​</a></h2><p><img src="'+g+'" alt="Edit Hosting Account"></p><p>You can modify:</p><ul><li><strong>Hosting Plan</strong> – Reassign the user to a different plan</li><li><strong>Reseller</strong> – Assign or change the reseller</li><li><strong>SSH Access</strong> – Toggle SSH on or off</li></ul><p>Click <strong>Submit</strong> to apply changes.</p><h2 id="changing-password" tabindex="-1">Changing Password <a class="header-anchor" href="#changing-password" aria-label="Permalink to &quot;Changing Password&quot;">​</a></h2><p><img src="'+d+'" alt="Change Password"></p><p>From the dropdown menu (⋮), select <strong>Change Password</strong>.</p><ul><li>Enter a new password and confirm it</li><li>Password strength will be displayed</li><li>You can auto-generate a secure password with the built-in generator</li><li>Click <strong>Submit</strong> to apply the new password</li></ul>',40)]))}const P=e(c,[["render",u]]);export{A as __pageData,P as default};
