import{_ as e,c as t,o as i,a2 as n}from"./chunks/framework.CHomSxuB.js";const s="/screenshots/hosting-plans.png",r="/screenshots/create-plan-general.png",o="/screenshots/hosting-parameters.png",l="/screenshots/plan-email-conf.png",g="/screenshots/plan-server-app-png.png",P=JSON.parse('{"title":"Hosting Plans","description":"","frontmatter":{},"headers":[{"level":3,"title":"1. General","slug":"_1-general","link":"#_1-general","children":[]},{"level":3,"title":"2. Hosting Parameters","slug":"_2-hosting-parameters","link":"#_2-hosting-parameters","children":[]},{"level":3,"title":"3. Email Configuration","slug":"_3-email-configuration","link":"#_3-email-configuration","children":[]},{"level":3,"title":"4. Server Application","slug":"_4-server-application","link":"#_4-server-application","children":[]}],"relativePath":"user-guide/admin-panel/hosting-plans.md","filePath":"user-guide/admin-panel/hosting-plans.md"}'),c={name:"user-guide/admin-panel/hosting-plans.md"};function u(d,a,p,m,h,b){return i(),t("div",null,a[0]||(a[0]=[n('<h1 id="hosting-plans" tabindex="-1">Hosting Plans <a class="header-anchor" href="#hosting-plans" aria-label="Permalink to &quot;Hosting Plans&quot;">​</a></h1><p>The <strong>Hosting Plans</strong> section allows administrators to create and manage custom hosting packages with specific resource limits, domain settings, email quotas, and server application configurations.</p><p><img src="'+s+'" alt="Hosting Plans List"></p><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p><strong>URL:</strong> <code>/admin/hosting-plans</code></p><p>This page displays a table of all existing hosting plans, showing key information such as:</p><ul><li><strong>Name</strong> – Hosting plan name</li><li><strong>Disk space</strong> – Maximum allowed disk space in MB</li><li><strong>Bandwidth</strong> – Maximum monthly data transfer in MB</li><li><strong>Databases</strong> – Number of databases included in the plan</li><li><strong>FTP accounts</strong> – Number of allowed FTP accounts</li><li><strong>Owner</strong> – User who created the plan</li></ul><blockquote><p>Additional columns <strong>Created At</strong> and <strong>Updated At</strong> are available through the column visibility toggle.</p></blockquote><ul><li><strong>Created At</strong> – Timestamp when the plan was created</li><li><strong>Updated At</strong> – Timestamp of the last update</li></ul><p>Action buttons available:</p><ul><li><strong>Edit</strong> – Modify the plan details</li><li><strong>Delete</strong> – Permanently delete the plan</li></ul><h2 id="search-and-filters" tabindex="-1">Search and Filters <a class="header-anchor" href="#search-and-filters" aria-label="Permalink to &quot;Search and Filters&quot;">​</a></h2><ul><li><strong>Search</strong> – Works by filtering the table based on the <strong>Name</strong> column.</li><li><strong>Filter by Reseller</strong> – A dropdown filter is available next to the search bar to display plans for a specific reseller.</li><li><strong>Reset Filter</strong> – A reset button clears all filters and restores the default table view.</li></ul><h2 id="creating-a-hosting-plan" tabindex="-1">Creating a Hosting Plan <a class="header-anchor" href="#creating-a-hosting-plan" aria-label="Permalink to &quot;Creating a Hosting Plan&quot;">​</a></h2><p>Clicking the <strong>Create Hosting Plan</strong> button opens a multi-tab modal dialog.</p><h3 id="_1-general" tabindex="-1">1. General <a class="header-anchor" href="#_1-general" aria-label="Permalink to &quot;1. General&quot;">​</a></h3><p><img src="'+r+'" alt="Create - General"></p><ul><li><strong>Name</strong> <em>(required)</em> – A unique, descriptive name for the plan (e.g. “Basic Hosting”, “Business Pro”)</li></ul><h3 id="_2-hosting-parameters" tabindex="-1">2. Hosting Parameters <a class="header-anchor" href="#_2-hosting-parameters" aria-label="Permalink to &quot;2. Hosting Parameters&quot;">​</a></h3><p><img src="'+o+'" alt="Hosting Parameters"></p><h4 id="storage-resources" tabindex="-1">Storage &amp; Resources <a class="header-anchor" href="#storage-resources" aria-label="Permalink to &quot;Storage &amp; Resources&quot;">​</a></h4><ul><li><strong>Disk Space</strong> – Maximum disk usage in MB</li><li><strong>Bandwidth</strong> – Monthly bandwidth in MB</li><li><strong>Databases</strong> – Number of databases</li><li><strong>FTP Accounts</strong> – Number of FTP users</li></ul><h4 id="domain-management" tabindex="-1">Domain Management <a class="header-anchor" href="#domain-management" aria-label="Permalink to &quot;Domain Management&quot;">​</a></h4><ul><li><strong>Subdomains</strong> – Maximum subdomains</li><li><strong>Parked Domains</strong> – Max parked domains</li><li><strong>Addon Domains</strong> – Max addon domains</li><li><strong>Free Domain</strong> – Number of free domains included</li></ul><h4 id="security-backups" tabindex="-1">Security &amp; Backups <a class="header-anchor" href="#security-backups" aria-label="Permalink to &quot;Security &amp; Backups&quot;">​</a></h4><ul><li><strong>SSL Certificates</strong> – Number of SSL certs</li><li><strong>Daily Backups</strong> – Number of daily backups included</li></ul><h3 id="_3-email-configuration" tabindex="-1">3. Email Configuration <a class="header-anchor" href="#_3-email-configuration" aria-label="Permalink to &quot;3. Email Configuration&quot;">​</a></h3><p><img src="'+l+'" alt="Email Configuration"></p><h4 id="email-accounts" tabindex="-1">Email Accounts <a class="header-anchor" href="#email-accounts" aria-label="Permalink to &quot;Email Accounts&quot;">​</a></h4><ul><li><strong>Email Accounts</strong> – Max number of email accounts</li><li><strong>Email Forwarders</strong> – Max forwarders</li><li><strong>Email Account Quota</strong> – Storage per account (MB)</li><li><strong>Email Filters</strong> – Max filters</li><li><strong>Email Autoresponders</strong> – Max autoresponders</li><li><strong>Mailing Lists</strong> – Max mailing lists</li></ul><h4 id="mailbox-settings" tabindex="-1">Mailbox Settings <a class="header-anchor" href="#mailbox-settings" aria-label="Permalink to &quot;Mailbox Settings&quot;">​</a></h4><ul><li><strong>Email Boxes</strong> – Max number of boxes</li><li><strong>Default Box Quota</strong> – Default storage (MB)</li><li><strong>Max Box Quota</strong> – Max allowed storage per box (MB)</li></ul><h3 id="_4-server-application" tabindex="-1">4. Server Application <a class="header-anchor" href="#_4-server-application" aria-label="Permalink to &quot;4. Server Application&quot;">​</a></h3><p><img src="'+g+'" alt="Server Application"></p><ul><li><strong>PHP Version</strong> – Select one of: <ul><li>PHP 7.4.33</li><li>PHP 8.2.29</li><li>PHP 8.4.10</li></ul></li><li><strong>Enable PHP-FPM</strong> – (Optional toggle, only visible for PHP 8.2.29 and PHP 8.4.10)</li></ul><h2 id="editing-an-existing-plan" tabindex="-1">Editing an Existing Plan <a class="header-anchor" href="#editing-an-existing-plan" aria-label="Permalink to &quot;Editing an Existing Plan&quot;">​</a></h2><p>Clicking the <strong>Edit</strong> button on any plan opens the same modal with pre-filled values.</p><p>Changes can be made to any of the tabs (General, Hosting Parameters, Email Configuration, Server Application). Once edited, click <strong>Save changes</strong> to apply.</p>',38)]))}const _=e(c,[["render",u]]);export{P as __pageData,_ as default};
