import{_ as i,c as t,o as a,a2 as s}from"./chunks/framework.B3hEhInr.js";const n="/screenshots/license.png",l="/screenshots/request-license.png",m=JSON.parse('{"title":"License Management","description":"","frontmatter":{},"headers":[{"level":3,"title":"License Key Activation","slug":"license-key-activation","link":"#license-key-activation","children":[]},{"level":3,"title":"Trial License","slug":"trial-license","link":"#trial-license","children":[]},{"level":3,"title":"License Status","slug":"license-status","link":"#license-status","children":[]},{"level":3,"title":"Need Help?","slug":"need-help","link":"#need-help","children":[]}],"relativePath":"user-guide/admin-panel/license.md","filePath":"user-guide/admin-panel/license.md"}'),r={name:"user-guide/admin-panel/license.md"};function o(c,e,d,u,h,p){return a(),t("div",null,e[0]||(e[0]=[s('<h1 id="license-management" tabindex="-1">License Management <a class="header-anchor" href="#license-management" aria-label="Permalink to &quot;License Management&quot;">​</a></h1><p>The <strong>License Management</strong> page allows you to enter, validate, and manage your AdminBolt license key to unlock premium features.</p><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p><strong>URL:</strong> <code>/admin/license</code></p><p>This page is divided into several key areas:</p><h3 id="license-key-activation" tabindex="-1">License Key Activation <a class="header-anchor" href="#license-key-activation" aria-label="Permalink to &quot;License Key Activation&quot;">​</a></h3><p>Users can activate or validate a license key using the <strong>License Key</strong> field and associated buttons.</p><ul><li><strong>Input Field</strong> – Enter your license key (case-sensitive, no spaces).</li><li><strong>Validate License Button</strong> – Sends the license key for validation.</li><li>If successful, the message shown is: <code>Success - License key is valid and has been applied!</code></li></ul><p><img src="'+n+'" alt="License Management Page"></p><h3 id="trial-license" tabindex="-1">Trial License <a class="header-anchor" href="#trial-license" aria-label="Permalink to &quot;Trial License&quot;">​</a></h3><p>If no license key is available, you can request a free trial license directly from the interface.</p><ul><li>Click the <strong>Request Trial License</strong> button to generate a free trial.</li><li>Trial licenses activate immediately and unlock all features temporarily.</li></ul><p><img src="'+l+'" alt="Request Trial License"></p><h3 id="license-status" tabindex="-1">License Status <a class="header-anchor" href="#license-status" aria-label="Permalink to &quot;License Status&quot;">​</a></h3><p>After a license is successfully validated, the current status is shown:</p><ul><li><p><strong>License Valid</strong> – Indicates that the license is active and working.</p></li><li><p><strong>Server IP</strong> – Displays the IP address the license is bound to.</p></li><li><p><strong>Expiration Date</strong> – Shows when the license will expire.</p></li><li><p><strong>Active License Key</strong> – Obfuscated display of the current license with optional reveal.</p></li><li><p><strong>Revalidate Button</strong> – Refreshes the validation of the currently active license key.</p><ul><li>If successful, the message shown is:<br><code>Success – License revalidated successfully!</code></li></ul></li><li><p><strong>Remove Button</strong> – Deletes the current license from the system.</p></li></ul><h3 id="need-help" tabindex="-1">Need Help? <a class="header-anchor" href="#need-help" aria-label="Permalink to &quot;Need Help?&quot;">​</a></h3><p>A help section is available at the bottom of the page with important tips:</p><ul><li>Contact support if validation fails.</li><li>Ensure no spaces or typos in the key.</li><li>Keys are case-sensitive and tied to your IP.</li><li>Click the refresh icon to revalidate.</li><li>Removing the license deletes all saved license data.</li></ul><h2 id="notes" tabindex="-1">Notes <a class="header-anchor" href="#notes" aria-label="Permalink to &quot;Notes&quot;">​</a></h2><ul><li>Revalidation is useful if your IP changes or you restore a server snapshot.</li></ul>',21)]))}const v=i(r,[["render",o]]);export{m as __pageData,v as default};
