import{_ as t,c as a,o,ag as l}from"./chunks/framework.Ds6Eueu6.js";const i="/screenshots/modules.png",p=JSON.parse('{"title":"Application Modules","description":"","frontmatter":{},"headers":[{"level":3,"title":"URL","slug":"url","link":"#url","children":[]},{"level":3,"title":"Search Modules","slug":"search-modules","link":"#search-modules","children":[]},{"level":3,"title":"List of Modules","slug":"list-of-modules","link":"#list-of-modules","children":[]}],"relativePath":"user-guide/admin-panel/modules.md","filePath":"user-guide/admin-panel/modules.md"}'),r={name:"user-guide/admin-panel/modules.md"};function s(n,e,d,u,c,h){return o(),a("div",null,e[0]||(e[0]=[l('<h1 id="application-modules" tabindex="-1">Application Modules <a class="header-anchor" href="#application-modules" aria-label="Permalink to &quot;Application Modules&quot;">​</a></h1><p>The <strong>Application Modules</strong> page allows administrators to manage modular extensions within the AdminBolt system. These modules provide additional functionality such as backup systems, security enhancements, automation, and third-party integrations.</p><p><img src="'+i+'" alt="Application Modules"></p><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p>This interface displays a list of available application modules that can be installed or removed. Each module includes a description, version information, category tags, and an install button if not already installed.</p><h3 id="url" tabindex="-1">URL <a class="header-anchor" href="#url" aria-label="Permalink to &quot;URL&quot;">​</a></h3><p><code>/admin/module-manager</code></p><h2 id="interface-elements" tabindex="-1">Interface Elements <a class="header-anchor" href="#interface-elements" aria-label="Permalink to &quot;Interface Elements&quot;">​</a></h2><h3 id="search-modules" tabindex="-1">Search Modules <a class="header-anchor" href="#search-modules" aria-label="Permalink to &quot;Search Modules&quot;">​</a></h3><ul><li><strong>Search Bar</strong> – Allows filtering the module list by name or keyword.</li></ul><h2 id="available-modules" tabindex="-1">Available Modules <a class="header-anchor" href="#available-modules" aria-label="Permalink to &quot;Available Modules&quot;">​</a></h2><p>Each module card contains:</p><ul><li><strong>Module Name</strong> – The name of the application module.</li><li><strong>Status</strong> – <code>Not Installed</code> or <code>Installed</code> (shown with a badge).</li><li><strong>Version</strong> – Displays the module version (e.g., <code>v0.1</code>).</li><li><strong>Description</strong> – Short summary of the module’s purpose.</li><li><strong>Tags</strong> – Categories for quick identification (e.g., <code>Security</code>, <code>Web Server</code>, <code>Backup</code>, etc.).</li><li><strong>Install Button</strong> – Triggers installation of the module.</li></ul><h3 id="list-of-modules" tabindex="-1">List of Modules <a class="header-anchor" href="#list-of-modules" aria-label="Permalink to &quot;List of Modules&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Module Name</th><th>Description</th><th>Tags</th></tr></thead><tbody><tr><td><strong>ModSecurity - CRS4 Rules</strong></td><td>Provides a set of curated security rules for ModSecurity.</td><td>Security, Web Server</td></tr><tr><td><strong>Softaculous</strong></td><td>Auto installer for web applications.</td><td>Automation, Installation</td></tr><tr><td><strong>JetBackup</strong></td><td>Backup solution for business continuity.</td><td>Backup, Security, Storage</td></tr><tr><td><strong>CloudLinux</strong></td><td>Linux-based OS for performance and isolation.</td><td>Operating System, Security, Performance</td></tr><tr><td><strong>Imunify360</strong></td><td>Security solution for Linux hosting environments.</td><td>Operating System, Security, Performance</td></tr></tbody></table>',15)]))}const g=t(r,[["render",s]]);export{p as __pageData,g as default};
