import{_ as t,c as n,o as i,ag as a}from"./chunks/framework.Ds6Eueu6.js";const s="/screenshots/multi-php-extensions.png",o="/screenshots/enable-disable-extension.png",u=JSON.parse('{"title":"Multi PHP Extensions","description":"","frontmatter":{},"headers":[{"level":3,"title":"Example:","slug":"example","link":"#example","children":[]}],"relativePath":"user-guide/admin-panel/php-extensions.md","filePath":"user-guide/admin-panel/php-extensions.md"}'),l={name:"user-guide/admin-panel/php-extensions.md"};function r(d,e,p,h,c,x){return i(),n("div",null,e[0]||(e[0]=[a('<h1 id="multi-php-extensions" tabindex="-1">Multi PHP Extensions <a class="header-anchor" href="#multi-php-extensions" aria-label="Permalink to &quot;Multi PHP Extensions&quot;">​</a></h1><p><img src="'+s+'" alt="Multi PHP Extensions"></p><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p><strong>URL:</strong><code>/admin/multi-p-h-p-extensions</code></p><p>The <strong>Multi PHP Extensions</strong> page allows you to control which PHP extensions are enabled for specific PHP versions installed on the server. This is especially useful when different domains or applications require different PHP capabilities.</p><p>You can:</p><ul><li>View all installed PHP versions</li><li>See which extensions are enabled for each version</li><li>Enable or disable extensions individually per PHP version</li></ul><h2 id="extension-list-by-php-version" tabindex="-1">Extension List by PHP Version <a class="header-anchor" href="#extension-list-by-php-version" aria-label="Permalink to &quot;Extension List by PHP Version&quot;">​</a></h2><p>Each row in the table represents a PHP version and a list of its currently enabled extensions. If the number of extensions is large, only a preview is shown with a link like “and X more”.</p><h3 id="example" tabindex="-1">Example: <a class="header-anchor" href="#example" aria-label="Permalink to &quot;Example:&quot;">​</a></h3><table tabindex="0"><thead><tr><th>PHP Version</th><th>Extensions</th><th>Action</th></tr></thead><tbody><tr><td>7.4.33</td><td>bz2, calendar, ctype, curl, exif, fileinfo, ftp, gettext, iconv, json...</td><td>Enable/Disable Extension</td></tr><tr><td>8.2.29</td><td>bz2, calendar, ctype, curl, dom, exif, fileinfo, ftp, gettext...</td><td>Enable/Disable Extension</td></tr><tr><td>8.4.10</td><td>bcmath, bz2, calendar, ctype, curl, dom, exif, fileinfo, ftp, gd...</td><td>Enable/Disable Extension</td></tr></tbody></table><h2 id="enabling-disabling-extensions" tabindex="-1">Enabling / Disabling Extensions <a class="header-anchor" href="#enabling-disabling-extensions" aria-label="Permalink to &quot;Enabling / Disabling Extensions&quot;">​</a></h2><p>Clicking <strong>Enable/Disable Extension</strong> opens a modal that displays a list of checkboxes for all available PHP extensions for that version.</p><p><img src="'+o+'" alt="Enable/Disable Extension Modal"></p><p>You can:</p><ul><li>Check or uncheck any extension</li><li>Click <strong>Submit</strong> to save the changes</li><li>Click <strong>Cancel</strong> to discard changes</li></ul><p>Changes take effect immediately after saving.</p><blockquote><p>If you want to add more PHP extensions, go to <strong>MyApache &gt; Profiles</strong> and create a new profile with the desired PHP version and extensions.</p></blockquote>',18)]))}const f=t(l,[["render",r]]);export{u as __pageData,f as default};
