import{_ as e,c as n,o as t,ag as o}from"./chunks/framework.Ds6Eueu6.js";const a="/screenshots/admin-php-ini-editor.png",g=JSON.parse('{"title":"PHP INI Editor","description":"","frontmatter":{},"headers":[{"level":3,"title":"Interface Elements","slug":"interface-elements","link":"#interface-elements","children":[]},{"level":3,"title":"PHP Version Selection","slug":"php-version-selection","link":"#php-version-selection","children":[]},{"level":3,"title":"Configuration File Options","slug":"configuration-file-options","link":"#configuration-file-options","children":[]},{"level":3,"title":"Configuration Editing","slug":"configuration-editing","link":"#configuration-editing","children":[]},{"level":3,"title":"Content Structure","slug":"content-structure","link":"#content-structure","children":[]},{"level":3,"title":"Management Actions","slug":"management-actions","link":"#management-actions","children":[]}],"relativePath":"user-guide/admin-panel/php-ini-editor.md","filePath":"user-guide/admin-panel/php-ini-editor.md"}'),l={name:"user-guide/admin-panel/php-ini-editor.md"};function r(s,i,c,d,u,h){return t(),n("div",null,i[0]||(i[0]=[o('<h1 id="php-ini-editor" tabindex="-1">PHP INI Editor <a class="header-anchor" href="#php-ini-editor" aria-label="Permalink to &quot;PHP INI Editor&quot;">​</a></h1><p>The PHP INI Editor provides a centralized interface to modify PHP configuration files across multiple PHP versions within your AdminBolt environment.</p><h2 id="php-ini-editor-overview" tabindex="-1">PHP INI Editor Overview <a class="header-anchor" href="#php-ini-editor-overview" aria-label="Permalink to &quot;PHP INI Editor Overview&quot;">​</a></h2><p><strong>URL:</strong> <code>/admin/multi-p-h-p-ini-editor</code><img src="'+a+'" alt="PHP INI Editor Interface"></p><h3 id="interface-elements" tabindex="-1">Interface Elements <a class="header-anchor" href="#interface-elements" aria-label="Permalink to &quot;Interface Elements&quot;">​</a></h3><h4 id="top-bar" tabindex="-1">Top Bar <a class="header-anchor" href="#top-bar" aria-label="Permalink to &quot;Top Bar&quot;">​</a></h4><ul><li><strong>PHP Configuration</strong> section heading</li></ul><h4 id="configuration-selection" tabindex="-1">Configuration Selection <a class="header-anchor" href="#configuration-selection" aria-label="Permalink to &quot;Configuration Selection&quot;">​</a></h4><ul><li><strong>PHP Version</strong> dropdown: Select from installed PHP versions (e.g., PHP 7.4.33)</li><li><strong>Configuration File</strong> dropdown: Select specific PHP configuration file (e.g., php.ini [MAIN])</li><li>Help text explaining configuration file selection</li></ul><h4 id="edit-configuration" tabindex="-1">Edit Configuration <a class="header-anchor" href="#edit-configuration" aria-label="Permalink to &quot;Edit Configuration&quot;">​</a></h4><ul><li><strong>Edit Configuration</strong> section heading</li><li>Guidance text for making changes to PHP configuration files</li><li><strong>PHP INI Content</strong> code editor with syntax highlighting</li><li>Expandable editor area for comfortable editing</li></ul><h4 id="editor-features" tabindex="-1">Editor Features <a class="header-anchor" href="#editor-features" aria-label="Permalink to &quot;Editor Features&quot;">​</a></h4><ul><li>Syntax highlighting for PHP INI format</li><li>Line numbers for easy reference</li><li>Comment highlighting in different color</li><li>Scrollable interface for long configuration files</li><li>Resizable editor area</li></ul><h4 id="php-ini-content" tabindex="-1">PHP INI Content <a class="header-anchor" href="#php-ini-content" aria-label="Permalink to &quot;PHP INI Content&quot;">​</a></h4><p>The editor displays the full PHP INI file content with:</p><ul><li>Section headers and comments</li><li>Configuration directives and values</li><li>Explanatory comments about PHP&#39;s initialization process</li><li>File paths and loading sequence information</li><li>Syntax guidelines and formatting rules</li></ul><h4 id="action-buttons" tabindex="-1">Action Buttons <a class="header-anchor" href="#action-buttons" aria-label="Permalink to &quot;Action Buttons&quot;">​</a></h4><ul><li><strong>Save Changes</strong>: Apply modifications to the PHP INI file</li><li><strong>Reset Changes</strong>: Revert to the original configuration</li></ul><h2 id="configuration-management" tabindex="-1">Configuration Management <a class="header-anchor" href="#configuration-management" aria-label="Permalink to &quot;Configuration Management&quot;">​</a></h2><h3 id="php-version-selection" tabindex="-1">PHP Version Selection <a class="header-anchor" href="#php-version-selection" aria-label="Permalink to &quot;PHP Version Selection&quot;">​</a></h3><p>The dropdown allows administrators to select from available PHP versions:</p><ul><li>PHP 7.x versions</li><li>PHP 8.x versions</li><li>Any custom-installed PHP versions</li></ul><h3 id="configuration-file-options" tabindex="-1">Configuration File Options <a class="header-anchor" href="#configuration-file-options" aria-label="Permalink to &quot;Configuration File Options&quot;">​</a></h3><p>The system offers different configuration files that can be edited:</p><ul><li>Main php.ini file</li><li>Additional .ini files specific to modules</li><li>Environment-specific configuration files</li></ul><h3 id="configuration-editing" tabindex="-1">Configuration Editing <a class="header-anchor" href="#configuration-editing" aria-label="Permalink to &quot;Configuration Editing&quot;">​</a></h3><p>The editor provides direct access to modify:</p><ul><li>PHP memory settings</li><li>Execution time limits</li><li>Error reporting levels</li><li>Extension loading</li><li>Session handling parameters</li><li>Upload limits and restrictions</li><li>Security settings</li><li>Module-specific configurations</li></ul><h3 id="content-structure" tabindex="-1">Content Structure <a class="header-anchor" href="#content-structure" aria-label="Permalink to &quot;Content Structure&quot;">​</a></h3><p>The PHP INI file typically contains:</p><ul><li>Descriptive comments explaining configuration options</li><li>Section divisions for related settings</li><li>Information about file loading sequence</li><li>Directory paths and environment variables</li><li>Module-specific settings</li></ul><h3 id="management-actions" tabindex="-1">Management Actions <a class="header-anchor" href="#management-actions" aria-label="Permalink to &quot;Management Actions&quot;">​</a></h3><ul><li>Apply changes directly to live PHP configuration</li><li>Reset modifications to default values</li><li>View syntax and formatting guidance within the file</li></ul><h2 id="management-benefits" tabindex="-1">Management Benefits <a class="header-anchor" href="#management-benefits" aria-label="Permalink to &quot;Management Benefits&quot;">​</a></h2><p>The PHP INI Editor streamlines server administration by providing:</p><ul><li>Direct access to PHP configuration without command line</li><li>Intuitive interface for complex configuration changes</li><li>Version-specific configuration management</li><li>Syntax highlighting for error prevention</li><li>Comprehensive configuration control</li><li>Educational comments explaining configuration parameters</li></ul>',36)]))}const p=e(l,[["render",r]]);export{g as __pageData,p as default};
