import{_ as i,c as o,o as l,a2 as r}from"./chunks/framework.CHomSxuB.js";const a="/screenshots/my-apache-profiles.png",t="/screenshots/create-myapache-profile-name.png",s="/screenshots/apache-mpm.png",n="/screenshots/apache-modules.png",p="/screenshots/php-versions.png",c="/screenshots/php-extensions.png",h="/screenshots/apache-review.png",u="/screenshots/apache-import-profile.png",q=JSON.parse('{"title":"Profiles","description":"","frontmatter":{},"headers":[{"level":3,"title":"Top Controls","slug":"top-controls","link":"#top-controls","children":[]},{"level":3,"title":"List Columns","slug":"list-columns","link":"#list-columns","children":[]},{"level":3,"title":"Profile Name","slug":"profile-name","link":"#profile-name","children":[]},{"level":3,"title":"1. Apache MPM","slug":"_1-apache-mpm","link":"#_1-apache-mpm","children":[]},{"level":3,"title":"2. Apache Modules","slug":"_2-apache-modules","link":"#_2-apache-modules","children":[]},{"level":3,"title":"3. PHP Versions","slug":"_3-php-versions","link":"#_3-php-versions","children":[]},{"level":3,"title":"4. PHP Extensions","slug":"_4-php-extensions","link":"#_4-php-extensions","children":[]},{"level":3,"title":"5. Review","slug":"_5-review","link":"#_5-review","children":[]}],"relativePath":"user-guide/admin-panel/profiles.md","filePath":"user-guide/admin-panel/profiles.md"}'),d={name:"user-guide/admin-panel/profiles.md"};function g(m,e,f,P,_,b){return l(),o("div",null,e[0]||(e[0]=[r('<h1 id="profiles" tabindex="-1">Profiles <a class="header-anchor" href="#profiles" aria-label="Permalink to &quot;Profiles&quot;">​</a></h1><p>The <strong>Profiles</strong> page allows you to manage Apache configurations through reusable profiles. Each profile defines the Apache MPM type, enabled modules, PHP versions, PHP extensions, and provisioning behavior.</p><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p><strong>URL:</strong> <code>/admin/my-apache-profiles</code></p><p><img src="'+a+'" alt="MyApache Profiles List"></p><p>This page lists all configured profiles with details like active packages and default status</p><h3 id="top-controls" tabindex="-1">Top Controls <a class="header-anchor" href="#top-controls" aria-label="Permalink to &quot;Top Controls&quot;">​</a></h3><ul><li><strong>Create MyApache Profile</strong> – Full configuration wizard</li><li><strong>Quick Create</strong> – Instantly provisions a default profile with recommended settings</li><li><strong>Import Profile</strong> – Upload JSON to restore/exported configuration</li></ul><h3 id="list-columns" tabindex="-1">List Columns <a class="header-anchor" href="#list-columns" aria-label="Permalink to &quot;List Columns&quot;">​</a></h3><ul><li><strong>Name</strong> – Profile identifier</li><li><strong>Packages</strong> – Installed active modules/extensions</li><li><strong>Is Default</strong> – Whether this is the server default profile or custom</li><li><strong>Actions</strong>: <ul><li><code>Edit</code></li><li><code>Delete</code> (Only for non-default profiles)</li><li><code>Export</code> (Export profile in .json)</li></ul></li></ul><h2 id="creating-a-profile" tabindex="-1">Creating a Profile <a class="header-anchor" href="#creating-a-profile" aria-label="Permalink to &quot;Creating a Profile&quot;">​</a></h2><p><strong>Trigger:</strong> <code>Create MyApache Profile</code> button</p><p>The creation flow opens a step-by-step wizard interface:</p><h3 id="profile-name" tabindex="-1">Profile Name <a class="header-anchor" href="#profile-name" aria-label="Permalink to &quot;Profile Name&quot;">​</a></h3><p><img src="'+t+'" alt="MyApache Profile Name"></p><p>Before configuring the profile, you are required to specify a <strong>Profile Name</strong>:</p><ul><li><strong>Name</strong> – Text field to identify this MyApache profile</li><li>A short description is shown:<br><em>“This name will be used to identify this MyApache profile.”</em></li></ul><h3 id="_1-apache-mpm" tabindex="-1">1. Apache MPM <a class="header-anchor" href="#_1-apache-mpm" aria-label="Permalink to &quot;1. Apache MPM&quot;">​</a></h3><p><img src="'+s+'" alt="MyApache MPM"></p><p>Choose MPM (Multi-Processing Module) type</p><p><strong>Features:</strong></p><ul><li>Toggle switches</li><li>Source labels (e.g., &quot;AdminBolt&quot;)</li><li>Description</li><li>Name</li><li>Search bar</li><li>Pagination and per-page selector</li><li>Filtering by source</li></ul><h3 id="_2-apache-modules" tabindex="-1">2. Apache Modules <a class="header-anchor" href="#_2-apache-modules" aria-label="Permalink to &quot;2. Apache Modules&quot;">​</a></h3><p><img src="'+n+'" alt="MyApache Modules"></p><p>Choose specific Apache module</p><p><strong>Features:</strong></p><ul><li>Toggle switches</li><li>Source labels (e.g., &quot;BOLT&quot;)</li><li>Description</li><li>Name</li><li>Search bar</li><li>Pagination and per-page selector</li><li>Filtering by source</li></ul><h3 id="_3-php-versions" tabindex="-1">3. PHP Versions <a class="header-anchor" href="#_3-php-versions" aria-label="Permalink to &quot;3. PHP Versions&quot;">​</a></h3><p><img src="'+p+'" alt="MyApache PHP Versions"></p><p>Choose desired PHP versions</p><p><strong>Features:</strong></p><ul><li>Toggle switches</li><li>Source labels (e.g., &quot;remi-safe&quot;)</li><li>Description</li><li>Name</li><li>Search bar</li><li>Pagination and per-page selector</li><li>Filtering by source</li></ul><h3 id="_4-php-extensions" tabindex="-1">4. PHP Extensions <a class="header-anchor" href="#_4-php-extensions" aria-label="Permalink to &quot;4. PHP Extensions&quot;">​</a></h3><p><img src="'+c+'" alt="MyApache PHP Extensions"></p><p>Choose PHP functionality with version-specific extensions</p><p><strong>Features:</strong></p><ul><li>Toggle switches</li><li>Source labels (e.g., &quot;remi-safe&quot;)</li><li>Description</li><li>Name</li><li>Search bar</li><li>Pagination and per-page selector</li><li>Filtering by source</li></ul><h3 id="_5-review" tabindex="-1">5. Review <a class="header-anchor" href="#_5-review" aria-label="Permalink to &quot;5. Review&quot;">​</a></h3><p><img src="'+h+'" alt="MyApache Review"></p><p>Displays a summary of all selected packages.</p><p><strong>Includes:</strong></p><ul><li>Warnings (if no packages are selected)</li><li>Component overview (multi-column layout)</li></ul><p><strong>Actions:</strong></p><ul><li><code>Finish</code> (Begins the provisioning process)</li><li><code>Previous</code> (Return to the previous step)</li></ul><h2 id="quick-create" tabindex="-1">Quick Create <a class="header-anchor" href="#quick-create" aria-label="Permalink to &quot;Quick Create&quot;">​</a></h2><p><strong>Trigger:</strong> <code>Quick Create</code> button</p><p>Launches the same wizard but pre-checks all recommended values</p><p>Identical tab structure as manual creation, just pre-filled.</p><h2 id="import-profile" tabindex="-1">Import Profile <a class="header-anchor" href="#import-profile" aria-label="Permalink to &quot;Import Profile&quot;">​</a></h2><p><strong>Trigger:</strong> <code>Import Profile</code> button</p><p>Allows importing a profile from a previously exported JSON file.</p><p><img src="'+u+'" alt="Import Profile"></p><p>Fields:</p><ul><li><strong>Profile Name</strong> – Label for the imported profile</li><li><strong>Apache Profile JSON</strong> – Drag/drop or Browse to select JSON</li></ul><p>Actions:</p><ul><li><code>Submit</code> – Apply imported configuration</li><li><code>Cancel</code></li></ul><h2 id="edit-profile" tabindex="-1">Edit Profile <a class="header-anchor" href="#edit-profile" aria-label="Permalink to &quot;Edit Profile&quot;">​</a></h2><p><strong>Trigger:</strong> <code>Edit</code> action from list</p><p>Launches the same step-by-step wizard with all values pre-filled.<br> User can:</p><ul><li>Adjust any tab</li><li>Re-provision</li><li>Export updated config</li></ul>',60)]))}const k=i(d,[["render",g]]);export{q as __pageData,k as default};
