import{_ as a,c as t,o as r,a2 as n}from"./chunks/framework.CHomSxuB.js";const l="/screenshots/reseller-accounts.png",o="/screenshots/create-reseller.png",s="/screenshots/impersonate-reseller.png",i="/screenshots/edit-reseller.png",b=JSON.parse('{"title":"Reseller Accounts","description":"","frontmatter":{},"headers":[{"level":3,"title":"Actions","slug":"actions","link":"#actions","children":[]},{"level":3,"title":"Fields","slug":"fields","link":"#fields","children":[]}],"relativePath":"user-guide/admin-panel/reseller-accounts.md","filePath":"user-guide/admin-panel/reseller-accounts.md"}'),c={name:"user-guide/admin-panel/reseller-accounts.md"};function g(d,e,u,h,p,m){return r(),t("div",null,e[0]||(e[0]=[n('<h1 id="reseller-accounts" tabindex="-1">Reseller Accounts <a class="header-anchor" href="#reseller-accounts" aria-label="Permalink to &quot;Reseller Accounts&quot;">​</a></h1><p>The <strong>Reseller Accounts</strong> section allows administrators to create, manage, and impersonate reseller accounts.</p><p><img src="'+l+'" alt="Reseller Accounts Page"></p><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p><strong>URL:</strong> <code>/admin/resellers</code></p><p>This page displays a list of all reseller accounts along with the following columns:</p><ul><li><strong>Name</strong> – Display name of the reseller</li><li><strong>Username</strong> – System username used for login</li><li><strong>Hosting Plans</strong> – Total number of hosting plans created by the reseller</li><li><strong>Hosting Accounts</strong> – Total number of hosting accounts managed by the reseller</li></ul><h3 id="actions" tabindex="-1">Actions <a class="header-anchor" href="#actions" aria-label="Permalink to &quot;Actions&quot;">​</a></h3><ul><li><strong>Login</strong> – Impersonates the reseller and opens their dashboard</li><li><strong>Edit</strong> – Update reseller info and credentials</li><li><strong>Delete</strong> – Permanently removes the reseller account</li></ul><h2 id="search-and-filtering" tabindex="-1">Search and Filtering <a class="header-anchor" href="#search-and-filtering" aria-label="Permalink to &quot;Search and Filtering&quot;">​</a></h2><ul><li>Individual search boxes are available for: <ul><li><strong>Name</strong></li><li><strong>Username</strong></li></ul></li></ul><p>Each input filters the list dynamically based on entered text.</p><h2 id="pagination" tabindex="-1">Pagination <a class="header-anchor" href="#pagination" aria-label="Permalink to &quot;Pagination&quot;">​</a></h2><p>Pagination controls allow you to set how many records to display per page:</p><ul><li>Options: <strong>5</strong>, <strong>10</strong>, <strong>25</strong>, <strong>50</strong>, <strong>All</strong></li></ul><h2 id="creating-a-reseller-account" tabindex="-1">Creating a Reseller Account <a class="header-anchor" href="#creating-a-reseller-account" aria-label="Permalink to &quot;Creating a Reseller Account&quot;">​</a></h2><p>Clicking the <strong>Create Reseller Account</strong> button opens the creation form.</p><p><img src="'+o+'" alt="Create Reseller"></p><h3 id="fields" tabindex="-1">Fields <a class="header-anchor" href="#fields" aria-label="Permalink to &quot;Fields&quot;">​</a></h3><h4 id="reseller-information" tabindex="-1">Reseller Information <a class="header-anchor" href="#reseller-information" aria-label="Permalink to &quot;Reseller Information&quot;">​</a></h4><ul><li><strong>Name</strong> <em>(required)</em> – Full name for display</li><li><strong>Username</strong> <em>(required)</em> – Login username</li><li><strong>Password</strong> <em>(required)</em> – Secure password with strength validation</li><li><strong>Password Again</strong> <em>(required)</em> – Confirmation of password</li></ul><p>You can click <strong>Generate password</strong> to auto-generate secure credentials.</p><h4 id="contact-details-optional" tabindex="-1">Contact Details (optional) <a class="header-anchor" href="#contact-details-optional" aria-label="Permalink to &quot;Contact Details (optional)&quot;">​</a></h4><ul><li><strong>Email</strong> – Primary email address</li><li><strong>Phone</strong> – Contact number</li></ul><h4 id="company-information-optional" tabindex="-1">Company Information (optional) <a class="header-anchor" href="#company-information-optional" aria-label="Permalink to &quot;Company Information (optional)&quot;">​</a></h4><ul><li><strong>Company</strong> – Business or organization name</li></ul><p>Buttons available:</p><ul><li><strong>Create</strong> – Save the new reseller</li><li><strong>Create &amp; Create Another</strong> – Save and reopen the form</li><li><strong>Cancel</strong> – Close without saving</li></ul><h2 id="impersonating-a-reseller" tabindex="-1">Impersonating a Reseller <a class="header-anchor" href="#impersonating-a-reseller" aria-label="Permalink to &quot;Impersonating a Reseller&quot;">​</a></h2><p>Clicking the <strong>Login</strong> button next to a reseller account allows the admin to impersonate that reseller.</p><p><img src="'+s+'" alt="Impersonate Reseller"></p><p>While impersonating:</p><ul><li>The top of the screen will display a blue bar with <strong>&quot;Impersonating user {username}&quot;</strong></li><li>The interface switches to the reseller panel layout</li><li>To stop impersonating, click the <strong>Leave</strong> button in the top bar</li></ul><h2 id="editing-a-reseller-account" tabindex="-1">Editing a Reseller Account <a class="header-anchor" href="#editing-a-reseller-account" aria-label="Permalink to &quot;Editing a Reseller Account&quot;">​</a></h2><p>Clicking <strong>Edit</strong> opens the form with all fields pre-filled.</p><p><img src="'+i+'" alt="Edit Reseller"></p><p>You can update:</p><ul><li>Name</li><li>Username</li><li>Password</li><li>Contact and company details</li></ul><p>Click <strong>Save changes</strong> to apply updates.</p>',39)]))}const q=a(c,[["render",g]]);export{b as __pageData,q as default};
