import{_ as s,c as t,o,ag as a}from"./chunks/framework.Ds6Eueu6.js";const r="/screenshots/ssh-access.png",S=JSON.parse('{"title":"SSH Access Configuration","description":"","frontmatter":{},"headers":[{"level":3,"title":"Enable SSH/SFTP","slug":"enable-ssh-sftp","link":"#enable-ssh-sftp","children":[]},{"level":3,"title":"SSH Port","slug":"ssh-port","link":"#ssh-port","children":[]},{"level":3,"title":"Allowed IP Addresses","slug":"allowed-ip-addresses","link":"#allowed-ip-addresses","children":[]},{"level":3,"title":"Permit Root Login","slug":"permit-root-login","link":"#permit-root-login","children":[]}],"relativePath":"user-guide/admin-panel/ssh-access.md","filePath":"user-guide/admin-panel/ssh-access.md"}'),n={name:"user-guide/admin-panel/ssh-access.md"};function i(l,e,c,d,h,p){return o(),t("div",null,e[0]||(e[0]=[a('<h1 id="ssh-access-configuration" tabindex="-1">SSH Access Configuration <a class="header-anchor" href="#ssh-access-configuration" aria-label="Permalink to &quot;SSH Access Configuration&quot;">​</a></h1><p>The <strong>SSH Access Configuration</strong> page allows administrators to securely manage and control SSH and SFTP access to the server.</p><p><img src="'+r+'" alt="SSH Access Configuration"></p><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p><strong>URL:</strong> <code>/admin/ssh-access</code></p><p>This page provides settings to control the SSH/SFTP service, SSH port, allowed IP addresses, and root login permissions.</p><h2 id="ssh-access-control" tabindex="-1">SSH Access Control <a class="header-anchor" href="#ssh-access-control" aria-label="Permalink to &quot;SSH Access Control&quot;">​</a></h2><h3 id="enable-ssh-sftp" tabindex="-1">Enable SSH/SFTP <a class="header-anchor" href="#enable-ssh-sftp" aria-label="Permalink to &quot;Enable SSH/SFTP&quot;">​</a></h3><p><strong>Type:</strong> Toggle<br><strong>Description:</strong> Enables or disables SSH and SFTP access to the server. When disabled, all incoming SSH/SFTP connections are blocked.</p><h3 id="ssh-port" tabindex="-1">SSH Port <a class="header-anchor" href="#ssh-port" aria-label="Permalink to &quot;SSH Port&quot;">​</a></h3><p><strong>Type:</strong> Input (number)<br><strong>Default:</strong> <code>22</code><br><strong>Description:</strong> The TCP port used by the SSH daemon. Changing the default port can help reduce automated attacks. Use a non-standard port only if you understand the implications.</p><h2 id="access-restrictions" tabindex="-1">Access Restrictions <a class="header-anchor" href="#access-restrictions" aria-label="Permalink to &quot;Access Restrictions&quot;">​</a></h2><h3 id="allowed-ip-addresses" tabindex="-1">Allowed IP Addresses <a class="header-anchor" href="#allowed-ip-addresses" aria-label="Permalink to &quot;Allowed IP Addresses&quot;">​</a></h3><p><strong>Type:</strong> Multi-IP input field<br><strong>Description:</strong> Specify IP addresses that are allowed to access the server via SSH.<br> If left empty, SSH is open to all IPs — which is <strong>not recommended for production</strong> environments.<br> Supports multiple IPs with add/remove capability.</p><h3 id="permit-root-login" tabindex="-1">Permit Root Login <a class="header-anchor" href="#permit-root-login" aria-label="Permalink to &quot;Permit Root Login&quot;">​</a></h3><p><strong>Type:</strong> Toggle<br><strong>Description:</strong> Allows or denies direct SSH access as the <code>root</code> user.<br><strong>Security Tip:</strong> It is highly recommended to disable this option to prevent potential attacks. Use a non-root user with <code>sudo</code> privileges instead.</p><h2 id="save-settings" tabindex="-1">Save Settings <a class="header-anchor" href="#save-settings" aria-label="Permalink to &quot;Save Settings&quot;">​</a></h2><p>Click the <strong>Save Settings</strong> button to apply any changes made on this page.</p><h2 id="notes" tabindex="-1">Notes <a class="header-anchor" href="#notes" aria-label="Permalink to &quot;Notes&quot;">​</a></h2><ul><li>Disabling root login and restricting IPs are best practices for securing SSH access.</li></ul>',20)]))}const u=s(n,[["render",i]]);export{S as __pageData,u as default};
