import{_ as e,c as a,o,a2 as n}from"./chunks/framework.CHomSxuB.js";const s="/screenshots/ssl-hosting-accounts.png",m=JSON.parse('{"title":"Hosting Accounts (AutoSSL Management)","description":"","frontmatter":{},"headers":[{"level":3,"title":"URL","slug":"url","link":"#url","children":[]}],"relativePath":"user-guide/admin-panel/ssl-hosting-accounts.md","filePath":"user-guide/admin-panel/ssl-hosting-accounts.md"}'),i={name:"user-guide/admin-panel/ssl-hosting-accounts.md"};function r(c,t,l,u,h,d){return o(),a("div",null,t[0]||(t[0]=[n('<h1 id="hosting-accounts-autossl-management" tabindex="-1">Hosting Accounts (AutoSSL Management) <a class="header-anchor" href="#hosting-accounts-autossl-management" aria-label="Permalink to &quot;Hosting Accounts (AutoSSL Management)&quot;">​</a></h1><p>The <strong>Hosting Accounts</strong> page allows administrators to enable or disable the AutoSSL feature for individual hosting accounts in the system.</p><p><img src="'+s+'" alt="Hosting Accounts"></p><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p>AutoSSL is used to automatically issue and install SSL/TLS certificates for hosted domains. On this page, you can manage AutoSSL status per account, giving you granular control over which accounts benefit from automatic certificate management.</p><h3 id="url" tabindex="-1">URL <a class="header-anchor" href="#url" aria-label="Permalink to &quot;URL&quot;">​</a></h3><p><code>/admin/auto-ssl-hosting-accounts</code></p><h2 id="description" tabindex="-1">Description <a class="header-anchor" href="#description" aria-label="Permalink to &quot;Description&quot;">​</a></h2><p>Each row in the table represents a hosting account. The toggle in the <strong>AutoSSL</strong> column determines whether that specific account is included in the AutoSSL issuance process.</p><p>This can be useful in scenarios where:</p><ul><li>You want to disable AutoSSL for accounts that use custom certificates.</li><li>You need to troubleshoot certificate issues per account.</li><li>A customer explicitly disables automatic SSL provisioning.</li></ul><h2 id="interface-elements" tabindex="-1">Interface Elements <a class="header-anchor" href="#interface-elements" aria-label="Permalink to &quot;Interface Elements&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Element</th><th>Description</th></tr></thead><tbody><tr><td><strong>User</strong></td><td>The username of the hosting account.</td></tr><tr><td><strong>AutoSSL</strong></td><td>A toggle that enables or disables automatic SSL for the selected account.</td></tr><tr><td><strong>Search Bar</strong></td><td>Allows you to search accounts by username.</td></tr><tr><td><strong>Per Page</strong></td><td>Sets how many accounts to display per page (pagination control).</td></tr></tbody></table><h2 id="actions" tabindex="-1">Actions <a class="header-anchor" href="#actions" aria-label="Permalink to &quot;Actions&quot;">​</a></h2><ul><li><strong>Toggle AutoSSL</strong> – Switch AutoSSL ON or OFF for each individual account.</li><li><strong>Search</strong> – Filter accounts by name to quickly locate the one you want to manage.</li></ul><h2 id="notes" tabindex="-1">Notes <a class="header-anchor" href="#notes" aria-label="Permalink to &quot;Notes&quot;">​</a></h2><ul><li>Changes made here are saved instantly.</li><li>This interface is ideal for fine-tuning SSL behavior on a per-client basis, especially in multi-tenant environments.</li></ul>',17)]))}const p=e(i,[["render",r]]);export{m as __pageData,p as default};
