import{_ as a,c as t,o as n,a2 as o}from"./chunks/framework.CHomSxuB.js";const r="/screenshots/system-settings.png",i="/screenshots/default-pages.png",s="/screenshots/supervisor.png",f=JSON.parse('{"title":"Settings","description":"","frontmatter":{},"headers":[{"level":3,"title":"Branding","slug":"branding","link":"#branding","children":[]},{"level":3,"title":"Organization Information","slug":"organization-information","link":"#organization-information","children":[]},{"level":3,"title":"Master Domain Page","slug":"master-domain-page","link":"#master-domain-page","children":[]},{"level":3,"title":"Domain Suspended Page","slug":"domain-suspended-page","link":"#domain-suspended-page","children":[]},{"level":3,"title":"Domain Created Page","slug":"domain-created-page","link":"#domain-created-page","children":[]},{"level":3,"title":"Worker Count","slug":"worker-count","link":"#worker-count","children":[]}],"relativePath":"user-guide/admin-panel/system-settings.md","filePath":"user-guide/admin-panel/system-settings.md"}'),l={name:"user-guide/admin-panel/system-settings.md"};function d(g,e,c,p,u,m){return n(),t("div",null,e[0]||(e[0]=[o('<h1 id="settings" tabindex="-1">Settings <a class="header-anchor" href="#settings" aria-label="Permalink to &quot;Settings&quot;">​</a></h1><p>The <strong>Settings</strong> page in AdminBolt provides a centralized interface to configure global branding, system-level content pages, and background process settings.</p><p><strong>URL:</strong> <code>/admin/general-settings</code></p><h2 id="general" tabindex="-1">General <a class="header-anchor" href="#general" aria-label="Permalink to &quot;General&quot;">​</a></h2><p>Customize the appearance and organizational information for your AdminBolt instance.</p><p><img src="'+r+'" alt="Settings - General Tab"></p><h3 id="branding" tabindex="-1">Branding <a class="header-anchor" href="#branding" aria-label="Permalink to &quot;Branding&quot;">​</a></h3><p>This section lets you define how your brand appears across the interface.</p><ul><li><strong>Brand Name</strong> – Sets the name of your brand, shown throughout the system.</li><li><strong>Brand Logo URL</strong> – Link to your logo image. Recommended size: <code>200x50px</code>.</li><li><strong>Primary Brand Color</strong> – Choose a primary color used for buttons, highlights, and links.</li></ul><h3 id="organization-information" tabindex="-1">Organization Information <a class="header-anchor" href="#organization-information" aria-label="Permalink to &quot;Organization Information&quot;">​</a></h3><p>Set the contact details for your organization.</p><ul><li><strong>Contact Email</strong> – Primary email for system alerts and notifications.</li><li><strong>Organization Name</strong> – The official name of your business or entity.</li><li><strong>Country</strong> – Your organization&#39;s country.</li><li><strong>City/Locality</strong> – Optional: specify the city or locality.</li></ul><p>After making changes, click <strong>Save Settings</strong> to apply them.</p><h2 id="default-pages" tabindex="-1">Default Pages <a class="header-anchor" href="#default-pages" aria-label="Permalink to &quot;Default Pages&quot;">​</a></h2><p>Define custom HTML content that will be shown for system-generated web server pages.</p><p><img src="'+i+'" alt="Settings - Default Pages Tab"></p><h3 id="master-domain-page" tabindex="-1">Master Domain Page <a class="header-anchor" href="#master-domain-page" aria-label="Permalink to &quot;Master Domain Page&quot;">​</a></h3><p>HTML content shown on the primary (master) domain.</p><h3 id="domain-suspended-page" tabindex="-1">Domain Suspended Page <a class="header-anchor" href="#domain-suspended-page" aria-label="Permalink to &quot;Domain Suspended Page&quot;">​</a></h3><p>HTML shown when a domain is suspended.</p><h3 id="domain-created-page" tabindex="-1">Domain Created Page <a class="header-anchor" href="#domain-created-page" aria-label="Permalink to &quot;Domain Created Page&quot;">​</a></h3><p>HTML displayed after a new domain is successfully provisioned.</p><blockquote><p>These pages support raw HTML (including <code>&lt;body&gt;</code>, <code>&lt;h1&gt;</code>, <code>&lt;script&gt;</code>, etc.) and can be used to reflect your branding or legal messaging.</p></blockquote><p>After editing, click <strong>Save Settings</strong>.</p><h2 id="supervisor" tabindex="-1">Supervisor <a class="header-anchor" href="#supervisor" aria-label="Permalink to &quot;Supervisor&quot;">​</a></h2><p>Manage background worker processes responsible for queued jobs and other system-level automation.</p><p><img src="'+s+'" alt="Settings - Supervisor Tab"></p><h3 id="worker-count" tabindex="-1">Worker Count <a class="header-anchor" href="#worker-count" aria-label="Permalink to &quot;Worker Count&quot;">​</a></h3><ul><li>Specifies the number of supervisor workers to spawn.</li><li>Default is <code>4</code>.</li><li>Increasing this may boost performance for heavy queue loads but requires more server resources.</li></ul><p>Click <strong>Save Settings</strong> to apply changes.</p>',30)]))}const b=a(l,[["render",d]]);export{f as __pageData,b as default};
