import{_ as t,c as o,o as a,a2 as r}from"./chunks/framework.CHomSxuB.js";const i="/assets/Customer_git-version-control-page.YeCIGyxK.png",g=JSON.parse('{"title":"Git™ Version Control – AdminBolt Hosting Panel","description":"","frontmatter":{},"headers":[{"level":3,"title":"Repository Table","slug":"repository-table","link":"#repository-table","children":[]},{"level":3,"title":"Create Repository","slug":"create-repository","link":"#create-repository","children":[]},{"level":3,"title":"Search","slug":"search","link":"#search","children":[]}],"relativePath":"user-guide/client-panel/git-version-control.md","filePath":"user-guide/client-panel/git-version-control.md"}'),l={name:"user-guide/client-panel/git-version-control.md"};function s(n,e,c,u,h,p){return a(),o("div",null,e[0]||(e[0]=[r('<h1 id="gittm-version-control-–-adminbolt-hosting-panel" tabindex="-1">Git™ Version Control – AdminBolt Hosting Panel <a class="header-anchor" href="#gittm-version-control-–-adminbolt-hosting-panel" aria-label="Permalink to &quot;Git™ Version Control – AdminBolt Hosting Panel&quot;">​</a></h1><p><img src="'+i+'" alt="Customer_git-version-control-page.png"></p><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p>The <strong>Git™ Version Control</strong> section in AdminBolt enables users to create and manage Git repositories directly from the hosting panel. This is essential for developers who want to track code changes, collaborate with others, and deploy projects seamlessly within the hosting environment.</p><h2 id="purpose" tabindex="-1">Purpose <a class="header-anchor" href="#purpose" aria-label="Permalink to &quot;Purpose&quot;">​</a></h2><ul><li>Initialize and manage Git repositories within your web hosting account.</li><li>Track changes across files using version control.</li><li>Support multi-user collaboration by storing development history.</li></ul><h2 id="interface-features" tabindex="-1">Interface Features <a class="header-anchor" href="#interface-features" aria-label="Permalink to &quot;Interface Features&quot;">​</a></h2><h3 id="repository-table" tabindex="-1">Repository Table <a class="header-anchor" href="#repository-table" aria-label="Permalink to &quot;Repository Table&quot;">​</a></h3><ul><li>A list that will show all created Git repositories.</li><li>Currently shows: <blockquote><p><strong>No Git Repositories</strong><br> “Create a git repository to get started.”</p></blockquote></li></ul><h3 id="create-repository" tabindex="-1">Create Repository <a class="header-anchor" href="#create-repository" aria-label="Permalink to &quot;Create Repository&quot;">​</a></h3><ul><li>An orange <strong>&quot;Create Repository&quot;</strong> button in the top-right corner.</li><li>Opens a form where you define: <ul><li>Repository name</li><li>Path/directory</li><li>Optional auto-deploy configurations</li></ul></li></ul><h3 id="search" tabindex="-1">Search <a class="header-anchor" href="#search" aria-label="Permalink to &quot;Search&quot;">​</a></h3><ul><li>Allows users to search for existing repositories by name or keyword.</li></ul><h2 id="usage-examples" tabindex="-1">Usage Examples <a class="header-anchor" href="#usage-examples" aria-label="Permalink to &quot;Usage Examples&quot;">​</a></h2><ul><li>Manage website source code under version control.</li><li>Collaborate with teams using Git commands via SSH.</li><li>Set up post-commit hooks to automatically deploy changes.</li><li>Keep backups of development progress without external tools.</li></ul><h2 id="sidebar-location" tabindex="-1">Sidebar Location <a class="header-anchor" href="#sidebar-location" aria-label="Permalink to &quot;Sidebar Location&quot;">​</a></h2><p><strong>Files → Git™ Version Control</strong></p><h2 id="related-features" tabindex="-1">Related Features <a class="header-anchor" href="#related-features" aria-label="Permalink to &quot;Related Features&quot;">​</a></h2><ul><li>SSH Terminal (for Git CLI access)</li><li>FTP Accounts (for manual uploads)</li><li>File Manager (for inspecting project files)</li></ul><h2 id="notes" tabindex="-1">Notes <a class="header-anchor" href="#notes" aria-label="Permalink to &quot;Notes&quot;">​</a></h2><ul><li>This feature supports <strong>local Git repositories</strong>, not direct integration with platforms like GitHub or GitLab (unless cloned via SSH).</li><li>Ideal for developers deploying Laravel, WordPress themes/plugins, or custom PHP/JS applications.</li></ul>',21)]))}const m=t(l,[["render",s]]);export{g as __pageData,m as default};
