import{_ as t,c as o,o as i,a2 as r}from"./chunks/framework.B3hEhInr.js";const s="/assets/Customer_hotlink-protection.C-yxps2f.png",p=JSON.parse('{"title":"Hotlink Protection – AdminBolt Hosting Panel","description":"","frontmatter":{},"headers":[],"relativePath":"user-guide/client-panel/hotlink-protection.md","filePath":"user-guide/client-panel/hotlink-protection.md"}'),l={name:"user-guide/client-panel/hotlink-protection.md"};function n(a,e,d,c,u,h){return i(),o("div",null,e[0]||(e[0]=[r('<h1 id="hotlink-protection-–-adminbolt-hosting-panel" tabindex="-1">Hotlink Protection – AdminBolt Hosting Panel <a class="header-anchor" href="#hotlink-protection-–-adminbolt-hosting-panel" aria-label="Permalink to &quot;Hotlink Protection – AdminBolt Hosting Panel&quot;">​</a></h1><p><img src="'+s+'" alt="Customer_hotlink-protection.png"></p><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p><strong>Hotlink Protection</strong> prevents other websites from directly linking to your hosted content (such as images, videos, and downloadable files). When enabled, it ensures that only specified domains can serve your content, which protects bandwidth usage and enhances security.</p><h2 id="features" tabindex="-1">Features <a class="header-anchor" href="#features" aria-label="Permalink to &quot;Features&quot;">​</a></h2><ul><li>Toggle Hotlink Protection (Enable/Disable)</li><li>Whitelist trusted URLs</li><li>Block specific file extensions (e.g., jpg, png, pdf)</li><li>Redirect unauthorized requests to a custom URL</li><li>Option to allow direct access (e.g., for media players)</li></ul><h2 id="ui-elements" tabindex="-1">UI Elements <a class="header-anchor" href="#ui-elements" aria-label="Permalink to &quot;UI Elements&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Element</th><th>Description</th></tr></thead><tbody><tr><td><strong>Enable Hotlink Protection</strong></td><td>Activates the feature (disabled by default)</td></tr><tr><td><strong>Add to URLs to allow access</strong></td><td>Opens a field to specify trusted domains (e.g., <code>example.com</code>)</td></tr><tr><td><strong>Block direct access for extensions</strong></td><td>Comma-separated list (e.g., <code>jpg,png,gif,mp4</code>)</td></tr><tr><td><strong>Allow direct requests</strong></td><td>Checkbox to allow file access without a referer (e.g., via QuickTime)</td></tr><tr><td><strong>Redirect the request to the following URL</strong></td><td>Optional redirect destination for blocked hotlink attempts</td></tr><tr><td><strong>Submit Button</strong></td><td>Saves and applies the configuration</td></tr></tbody></table><h2 id="how-to-use" tabindex="-1">How to Use <a class="header-anchor" href="#how-to-use" aria-label="Permalink to &quot;How to Use&quot;">​</a></h2><ol><li>Navigate to: <strong>Security → Hotlink Protection</strong></li><li>Click the <strong>Enable Hotlink Protection</strong> button.</li><li>Add trusted domains (e.g., your own sites) using the <strong>Add to URLs</strong> button.</li><li>In the extensions field, enter file types you want to protect (e.g., <code>jpg, png, mp4</code>).</li><li>(Optional) Check <strong>Allow direct requests</strong> if your media player requires it.</li><li>(Optional) Enter a <strong>redirect URL</strong> for blocked requests (e.g., <code>/no-hotlinking.html</code>).</li><li>Click <strong>Submit</strong> to save your settings.</li></ol><h2 id="use-cases" tabindex="-1">Use Cases <a class="header-anchor" href="#use-cases" aria-label="Permalink to &quot;Use Cases&quot;">​</a></h2><ul><li><strong>Prevent Image Theft</strong> – Block external sites from embedding your images</li><li><strong>Save Bandwidth</strong> – Avoid paying for resources used by other sites</li><li><strong>Improve SEO</strong> – Prevent duplicate content served via unauthorized URLs</li><li><strong>Redirect Offenders</strong> – Show custom warning pages for unauthorized hotlinking</li></ul><h2 id="best-practices" tabindex="-1">Best Practices <a class="header-anchor" href="#best-practices" aria-label="Permalink to &quot;Best Practices&quot;">​</a></h2><ul><li>Always whitelist your own domains</li><li>Use redirection to inform users or log attempts</li><li>Regularly review allowed URLs and blocked file types</li><li>Keep extensions list minimal to reduce false positives</li></ul><h2 id="troubleshooting" tabindex="-1">Troubleshooting <a class="header-anchor" href="#troubleshooting" aria-label="Permalink to &quot;Troubleshooting&quot;">​</a></h2><ul><li>If content isn’t loading on your site: <ul><li>Ensure your own domain is whitelisted</li><li>Check if “Allow direct requests” is required by your player/browser</li></ul></li><li>If blocked content isn’t redirecting: <ul><li>Verify the redirect URL is valid and publicly accessible</li></ul></li></ul><h2 id="related" tabindex="-1">Related <a class="header-anchor" href="#related" aria-label="Permalink to &quot;Related&quot;">​</a></h2><ul><li><a href="./ip-blockers.html">IP Blockers</a></li><li><a href="./ssl-statuses.html">SSL Statuses</a></li></ul>',18)]))}const m=t(l,[["render",n]]);export{p as __pageData,m as default};
