import{_ as t,c as o,o as r,a2 as i}from"./chunks/framework.CHomSxuB.js";const a="/assets/Customer_ip-blockers-page.CQwY1q0y.png",p=JSON.parse('{"title":"IP Blockers – AdminBolt Hosting Panel","description":"","frontmatter":{},"headers":[],"relativePath":"user-guide/client-panel/ip-blockers.md","filePath":"user-guide/client-panel/ip-blockers.md"}'),s={name:"user-guide/client-panel/ip-blockers.md"};function l(n,e,d,c,u,h){return r(),o("div",null,e[0]||(e[0]=[i('<h1 id="ip-blockers-–-adminbolt-hosting-panel" tabindex="-1">IP Blockers – AdminBolt Hosting Panel <a class="header-anchor" href="#ip-blockers-–-adminbolt-hosting-panel" aria-label="Permalink to &quot;IP Blockers – AdminBolt Hosting Panel&quot;">​</a></h1><p><img src="'+a+'" alt="Customer_ip-blockers-page.png"></p><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p>The <strong>IP Blockers</strong> feature enables you to restrict access to your website by blocking specific IP addresses or ranges. This is useful for preventing brute-force attacks, limiting access from malicious users, or enforcing geo-blocking rules.</p><h2 id="key-features" tabindex="-1">Key Features <a class="header-anchor" href="#key-features" aria-label="Permalink to &quot;Key Features&quot;">​</a></h2><ul><li>Block single IPs, IP ranges (CIDR), or domain names</li><li>Domain names are automatically resolved to IPs</li><li>Real-time update of blocked list</li><li>Search function for existing blocked entries</li></ul><h2 id="interface-description" tabindex="-1">Interface Description <a class="header-anchor" href="#interface-description" aria-label="Permalink to &quot;Interface Description&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Section</th><th>Description</th></tr></thead><tbody><tr><td>IP Address or Domain Field</td><td>Input box where the admin enters a target IP address or domain</td></tr><tr><td>Add IP Block Button</td><td>Submits the entered IP or domain to the block list</td></tr><tr><td>Currently-Blocked IP Addresses</td><td>Displays a list of all blocked IPs with search capability</td></tr><tr><td>Empty State Indicator</td><td>Shows “No IP blockers” if no addresses are blocked</td></tr></tbody></table><h2 id="how-to-use" tabindex="-1">How to Use <a class="header-anchor" href="#how-to-use" aria-label="Permalink to &quot;How to Use&quot;">​</a></h2><ol><li>Go to: <strong>Security → IP Blockers</strong></li><li>In the &quot;Add an IP or Range&quot; section, enter: <ul><li>A full IPv4/IPv6 address (e.g., <code>192.168.1.1</code>)</li><li>A subnet (e.g., <code>192.168.1.0/24</code>)</li><li>A domain (e.g., <code>baduser.example.com</code>)</li></ul></li><li>Click <strong>Add IP Block</strong></li><li>The entry appears in the <strong>Currently-Blocked IP Addresses</strong> list</li></ol><h2 id="use-cases" tabindex="-1">Use Cases <a class="header-anchor" href="#use-cases" aria-label="Permalink to &quot;Use Cases&quot;">​</a></h2><ul><li><strong>DDoS Protection</strong> – Block suspicious IP ranges flooding your server</li><li><strong>Geo-restriction</strong> – Block regions based on IP</li><li><strong>Abuse Control</strong> – Stop brute-force or repeated failed login attempts</li><li><strong>Blacklist Known Bots</strong> – Deny access to scraping/spam bots</li></ul><h2 id="security-notes" tabindex="-1">Security Notes <a class="header-anchor" href="#security-notes" aria-label="Permalink to &quot;Security Notes&quot;">​</a></h2><ul><li>IP blocks are enforced at the server level</li><li>DNS lookups are performed for domain entries</li><li>Admins should double-check accuracy to avoid false positives</li></ul><h2 id="troubleshooting" tabindex="-1">Troubleshooting <a class="header-anchor" href="#troubleshooting" aria-label="Permalink to &quot;Troubleshooting&quot;">​</a></h2><p>If blocks are not working:</p><ul><li>Ensure firewall rules are not overriding panel settings</li><li>Verify the domain resolves to a valid IP</li><li>Refresh the interface after adding blocks</li></ul><h2 id="related-sections" tabindex="-1">Related Sections <a class="header-anchor" href="#related-sections" aria-label="Permalink to &quot;Related Sections&quot;">​</a></h2><ul><li><a href="./hotlink-protection.html">Hotlink Protection</a></li><li><a href="./ssh-terminal.html">SSH Terminal</a></li></ul>',19)]))}const g=t(s,[["render",l]]);export{p as __pageData,g as default};
