import{_ as a,c as i,o as t,ag as l}from"./chunks/framework.Ds6Eueu6.js";const n="/assets/Customer_PHP_My_Admin.B2aDjddB.png",m=JSON.parse('{"title":"phpMyAdmin – AdminBolt Hosting Panel","description":"","frontmatter":{},"headers":[{"level":3,"title":"General Settings","slug":"general-settings","link":"#general-settings","children":[]},{"level":3,"title":"Appearance Settings","slug":"appearance-settings","link":"#appearance-settings","children":[]},{"level":3,"title":"Actions:","slug":"actions","link":"#actions","children":[]},{"level":3,"title":"Database Server","slug":"database-server","link":"#database-server","children":[]},{"level":3,"title":"Web Server","slug":"web-server","link":"#web-server","children":[]},{"level":3,"title":"phpMyAdmin Info","slug":"phpmyadmin-info","link":"#phpmyadmin-info","children":[]}],"relativePath":"user-guide/client-panel/phpmyadmin.md","filePath":"user-guide/client-panel/phpmyadmin.md"}'),s={name:"user-guide/client-panel/phpmyadmin.md"};function r(o,e,d,c,h,u){return t(),i("div",null,e[0]||(e[0]=[l('<h1 id="phpmyadmin-–-adminbolt-hosting-panel" tabindex="-1">phpMyAdmin – AdminBolt Hosting Panel <a class="header-anchor" href="#phpmyadmin-–-adminbolt-hosting-panel" aria-label="Permalink to &quot;phpMyAdmin – AdminBolt Hosting Panel&quot;">​</a></h1><p><img src="'+n+'" alt="Customer_PHP_My_Admin.png"></p><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p><strong>phpMyAdmin</strong> is a web-based database management tool integrated into AdminBolt. It provides an interface for users to manage MySQL or MariaDB databases using a graphical UI instead of command-line SQL queries.</p><h2 id="key-features" tabindex="-1">Key Features <a class="header-anchor" href="#key-features" aria-label="Permalink to &quot;Key Features&quot;">​</a></h2><ul><li>Browse, create, modify, or delete databases and tables.</li><li>Execute SQL queries interactively.</li><li>Import/export database dumps.</li><li>Manage indexes, relationships, users, and privileges.</li></ul><h2 id="general-layout" tabindex="-1">General Layout <a class="header-anchor" href="#general-layout" aria-label="Permalink to &quot;General Layout&quot;">​</a></h2><h3 id="general-settings" tabindex="-1">General Settings <a class="header-anchor" href="#general-settings" aria-label="Permalink to &quot;General Settings&quot;">​</a></h3><ul><li><strong>Change Password</strong>: Update your MySQL user password.</li><li><strong>Server Collation</strong>: Default collation (e.g., <code>utf8mb4_unicode_ci</code>).</li><li><strong>More Settings</strong>: Access deeper configuration options.</li></ul><h3 id="appearance-settings" tabindex="-1">Appearance Settings <a class="header-anchor" href="#appearance-settings" aria-label="Permalink to &quot;Appearance Settings&quot;">​</a></h3><ul><li><strong>Language Selector</strong>: Set interface language (e.g., English).</li><li><strong>Theme Selector</strong>: Choose between available UI themes.</li></ul><h2 id="sidebar-left-panel" tabindex="-1">Sidebar (Left Panel) <a class="header-anchor" href="#sidebar-left-panel" aria-label="Permalink to &quot;Sidebar (Left Panel)&quot;">​</a></h2><ul><li>Shows database tree: <ul><li><code>information_schema</code></li><li><code>mydomain...</code> (user-specific database)</li></ul></li></ul><h3 id="actions" tabindex="-1">Actions: <a class="header-anchor" href="#actions" aria-label="Permalink to &quot;Actions:&quot;">​</a></h3><ul><li>Expand/collapse tables</li><li>Run operations like Structure, Browse, Insert, Export, etc.</li></ul><h2 id="system-info-panels" tabindex="-1">System Info Panels <a class="header-anchor" href="#system-info-panels" aria-label="Permalink to &quot;System Info Panels&quot;">​</a></h2><h3 id="database-server" tabindex="-1">Database Server <a class="header-anchor" href="#database-server" aria-label="Permalink to &quot;Database Server&quot;">​</a></h3><ul><li><strong>Server</strong>: AdminBolt (localhost)</li><li><strong>Type</strong>: MariaDB</li><li><strong>Version</strong>: 10.5.27-MariaDB</li><li><strong>Protocol</strong>: 10</li><li><strong>Connection</strong>: Not using SSL</li></ul><h3 id="web-server" tabindex="-1">Web Server <a class="header-anchor" href="#web-server" aria-label="Permalink to &quot;Web Server&quot;">​</a></h3><ul><li><strong>Server</strong>: nginx/1.25.5</li><li><strong>Client</strong>: libmysql – mysqlnd 8.2.0</li><li><strong>PHP Extensions</strong>: <code>mysqli</code>, <code>curl</code>, <code>mbstring</code>, <code>sodium</code></li><li><strong>PHP Version</strong>: 8.2.0</li></ul><h3 id="phpmyadmin-info" tabindex="-1">phpMyAdmin Info <a class="header-anchor" href="#phpmyadmin-info" aria-label="Permalink to &quot;phpMyAdmin Info&quot;">​</a></h3><ul><li>Version: 5.2.1 (latest available: 5.2.2)</li><li>Quick access to: <ul><li>Documentation</li><li>Official Homepage</li><li>Change Log</li><li>License</li></ul></li></ul><h2 id="notices" tabindex="-1">Notices <a class="header-anchor" href="#notices" aria-label="Permalink to &quot;Notices&quot;">​</a></h2><ul><li><em>&quot;A newer version of phpMyAdmin is available...&quot;</em></li><li><em>&quot;The phpMyAdmin configuration storage is not completely configured...&quot;</em><br> This may disable some extended features unless manually set up.</li></ul><h2 id="access-location" tabindex="-1">Access Location <a class="header-anchor" href="#access-location" aria-label="Permalink to &quot;Access Location&quot;">​</a></h2><p><strong>Sidebar → Databases → phpMyAdmin</strong></p><h2 id="related-sections" tabindex="-1">Related Sections <a class="header-anchor" href="#related-sections" aria-label="Permalink to &quot;Related Sections&quot;">​</a></h2><ul><li><a href="./databases.html">Databases</a></li><li><a href="./database-wizard.html">Database Wizard</a></li></ul><h2 id="notes" tabindex="-1">Notes <a class="header-anchor" href="#notes" aria-label="Permalink to &quot;Notes&quot;">​</a></h2><ul><li>phpMyAdmin is ideal for both developers and non-technical users to safely manage databases.</li><li>For best results, users should ensure configuration storage is set and keep phpMyAdmin up to date.</li></ul>',30)]))}const g=a(s,[["render",r]]);export{m as __pageData,g as default};
