import{_ as e,c as a,o as i,a2 as o}from"./chunks/framework.CHomSxuB.js";const s="/assets/Customer_ssl-statuses.D9Spwd0B.png",S=JSON.parse('{"title":"SSL Statuses – AdminBolt Hosting Panel","description":"","frontmatter":{},"headers":[],"relativePath":"user-guide/client-panel/ssl-statuses.md","filePath":"user-guide/client-panel/ssl-statuses.md"}'),l={name:"user-guide/client-panel/ssl-statuses.md"};function n(r,t,d,c,u,h){return i(),a("div",null,t[0]||(t[0]=[o('<h1 id="ssl-statuses-–-adminbolt-hosting-panel" tabindex="-1">SSL Statuses – AdminBolt Hosting Panel <a class="header-anchor" href="#ssl-statuses-–-adminbolt-hosting-panel" aria-label="Permalink to &quot;SSL Statuses – AdminBolt Hosting Panel&quot;">​</a></h1><p><img src="'+s+'" alt="Customer_ssl-statuses.png"></p><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p>The <strong>SSL Statuses</strong> page allows users to monitor and manage SSL certificate status for all hosted domains. This is essential for ensuring secure HTTPS connections for websites and email services.</p><h2 id="key-features" tabindex="-1">Key Features <a class="header-anchor" href="#key-features" aria-label="Permalink to &quot;Key Features&quot;">​</a></h2><ul><li>View SSL status for all domains and subdomains</li><li>Toggle inclusion in AutoSSL (automatic SSL management)</li><li>Identify unsecure domains via visual indicators (red padlock)</li></ul><h2 id="interface-elements" tabindex="-1">Interface Elements <a class="header-anchor" href="#interface-elements" aria-label="Permalink to &quot;Interface Elements&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Element</th><th>Description</th></tr></thead><tbody><tr><td>Red Padlock Icon</td><td>Indicates the domain currently lacks a valid SSL certificate</td></tr><tr><td><strong>Domain Column</strong></td><td>Lists primary domains and subdomains managed under the hosting account</td></tr><tr><td><strong>Exclude/Include during AutoSSL</strong></td><td>Allows the user to toggle participation in the automatic SSL renewal process</td></tr><tr><td><strong>Checkbox (bulk select)</strong></td><td>Allows bulk actions for multiple domains (not yet implemented in image)</td></tr></tbody></table><h2 id="common-domain-types-listed" tabindex="-1">Common Domain Types Listed <a class="header-anchor" href="#common-domain-types-listed" aria-label="Permalink to &quot;Common Domain Types Listed&quot;">​</a></h2><ul><li><code>www.domain.com</code> – Standard website</li><li><code>mail.domain.com</code> – Mail server endpoint</li><li><code>autoconfig.</code> / <code>autodiscover.</code> – Email client auto-discovery endpoints</li><li><code>webmail.</code> – Webmail interface</li></ul><h2 id="managing-ssls" tabindex="-1">Managing SSLs <a class="header-anchor" href="#managing-ssls" aria-label="Permalink to &quot;Managing SSLs&quot;">​</a></h2><ol><li>Navigate to <strong>Security → SSL Statuses</strong></li><li>Review the list of domains</li><li>For each domain: <ul><li>If you want it managed by AutoSSL, ensure it shows: <strong>&quot;Include during AutoSSL&quot;</strong></li><li>To skip it, click the link to <strong>Exclude during AutoSSL</strong></li></ul></li><li>Domains without valid certificates will show a red padlock</li></ol><h2 id="best-practices" tabindex="-1">Best Practices <a class="header-anchor" href="#best-practices" aria-label="Permalink to &quot;Best Practices&quot;">​</a></h2><ul><li>Keep all active subdomains included in AutoSSL</li><li>Exclude only legacy/test subdomains that don’t need HTTPS</li><li>Use a wildcard SSL certificate for domains with many subdomains (optional)</li><li>Regularly check this page to verify certificate coverage</li></ul><h2 id="troubleshooting" tabindex="-1">Troubleshooting <a class="header-anchor" href="#troubleshooting" aria-label="Permalink to &quot;Troubleshooting&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Issue</th><th>Solution</th></tr></thead><tbody><tr><td>Red padlock shown</td><td>No valid SSL. Wait for AutoSSL or install manually</td></tr><tr><td>AutoSSL not working</td><td>Check DNS records or ensure domain is pointed correctly</td></tr><tr><td>Mail clients show SSL error</td><td>Make sure <code>mail.</code> and <code>autodiscover.</code> are covered by valid certificates</td></tr></tbody></table><h2 id="related" tabindex="-1">Related <a class="header-anchor" href="#related" aria-label="Permalink to &quot;Related&quot;">​</a></h2><ul><li><a href="./certificates.html">Certificates</a></li><li><a href="./hotlink-protection.html">Hotlink Protection</a></li></ul>',18)]))}const g=e(l,[["render",n]]);export{S as __pageData,g as default};
