import{_ as a,c as t,o as i,ag as o}from"./chunks/framework.Ds6Eueu6.js";const n="/assets/Customer_zone-editor-page.BlH5_hp0.png",p=JSON.parse('{"title":"Zone Editor – AdminBolt Hosting Panel","description":"","frontmatter":{},"headers":[{"level":3,"title":"Purpose","slug":"purpose","link":"#purpose","children":[]},{"level":3,"title":"Contextual Use","slug":"contextual-use","link":"#contextual-use","children":[]},{"level":3,"title":"Domain List","slug":"domain-list","link":"#domain-list","children":[]},{"level":3,"title":"Search Bar","slug":"search-bar","link":"#search-bar","children":[]},{"level":3,"title":"Pagination","slug":"pagination","link":"#pagination","children":[]}],"relativePath":"user-guide/client-panel/zone-editor.md","filePath":"user-guide/client-panel/zone-editor.md"}'),r={name:"user-guide/client-panel/zone-editor.md"};function l(s,e,d,h,c,u){return i(),t("div",null,e[0]||(e[0]=[o('<h1 id="zone-editor-–-adminbolt-hosting-panel" tabindex="-1">Zone Editor – AdminBolt Hosting Panel <a class="header-anchor" href="#zone-editor-–-adminbolt-hosting-panel" aria-label="Permalink to &quot;Zone Editor – AdminBolt Hosting Panel&quot;">​</a></h1><p><img src="'+n+'" alt="Customer_zone-editor-page.png"></p><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p>The <strong>Zone Editor</strong> in AdminBolt is a tool used to manage DNS records for each domain. DNS (Domain Name System) converts domain names into computer-readable IP addresses. This section allows the user to view and edit DNS zones for domains hosted under their account.</p><h2 id="description" tabindex="-1">Description <a class="header-anchor" href="#description" aria-label="Permalink to &quot;Description&quot;">​</a></h2><h3 id="purpose" tabindex="-1">Purpose <a class="header-anchor" href="#purpose" aria-label="Permalink to &quot;Purpose&quot;">​</a></h3><ul><li>Allows the user to control DNS configurations such as: <ul><li>A (Address) records</li><li>AAAA (IPv6) records</li><li>MX (Mail Exchange) records</li><li>CNAME (Canonical Name) records</li><li>TXT, SRV, and other DNS entries</li></ul></li></ul><h3 id="contextual-use" tabindex="-1">Contextual Use <a class="header-anchor" href="#contextual-use" aria-label="Permalink to &quot;Contextual Use&quot;">​</a></h3><ul><li>Essential for setting up mail servers, verifying domains with external providers, pointing domains/subdomains to other servers, or setting up CDN.</li></ul><h2 id="key-interface-elements" tabindex="-1">Key Interface Elements <a class="header-anchor" href="#key-interface-elements" aria-label="Permalink to &quot;Key Interface Elements&quot;">​</a></h2><h3 id="domain-list" tabindex="-1">Domain List <a class="header-anchor" href="#domain-list" aria-label="Permalink to &quot;Domain List&quot;">​</a></h3><ul><li>Lists all hosted domains under the current user account.</li><li>Each row includes: <ul><li><strong>Domain Name</strong></li><li><strong>Action Button</strong>: “Manage Zones”</li></ul></li></ul><h3 id="search-bar" tabindex="-1">Search Bar <a class="header-anchor" href="#search-bar" aria-label="Permalink to &quot;Search Bar&quot;">​</a></h3><ul><li>Allows filtering/searching for specific domains within the list.</li></ul><h3 id="pagination" tabindex="-1">Pagination <a class="header-anchor" href="#pagination" aria-label="Permalink to &quot;Pagination&quot;">​</a></h3><ul><li>Option to set number of entries per page (e.g. 10 per page).</li></ul><h2 id="example-domains-shown" tabindex="-1">Example Domains Shown <a class="header-anchor" href="#example-domains-shown" aria-label="Permalink to &quot;Example Domains Shown&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Domain</th><th>Action</th></tr></thead><tbody><tr><td>mydomain.com</td><td>Manage Zones</td></tr><tr><td>mydomain.info</td><td>Manage Zones</td></tr></tbody></table><h2 id="actions" tabindex="-1">Actions <a class="header-anchor" href="#actions" aria-label="Permalink to &quot;Actions&quot;">​</a></h2><ul><li>Click <strong>Manage Zones</strong> to access detailed DNS records for the selected domain.</li><li>Edit or delete existing records.</li><li>Add new custom DNS entries.</li></ul><h2 id="related-sections" tabindex="-1">Related Sections <a class="header-anchor" href="#related-sections" aria-label="Permalink to &quot;Related Sections&quot;">​</a></h2><ul><li><a href="./domains.html">Domains</a></li><li><a href="./dns-health.html">DNS Health</a></li></ul><h2 id="notes" tabindex="-1">Notes <a class="header-anchor" href="#notes" aria-label="Permalink to &quot;Notes&quot;">​</a></h2><ul><li>Located in the left sidebar under:<br><strong>Domains → Zone Editor</strong></li><li>Useful when integrating third-party services like email providers, Google Workspace, or external apps that require DNS verification.</li></ul>',24)]))}const g=a(r,[["render",l]]);export{p as __pageData,g as default};
