import{_ as e,c as i,o as t,ae as o}from"./chunks/framework.tuuL-SzM.js";const n="/screenshots/email-accounts-list.png",l="/screenshots/email-accounts-create.png",p=JSON.parse('{"title":"Email Management","description":"","frontmatter":{},"headers":[],"relativePath":"user-guide/customer-panel/email.md","filePath":"user-guide/customer-panel/email.md"}'),r={name:"user-guide/customer-panel/email.md"};function s(c,a,u,m,d,g){return t(),i("div",null,a[0]||(a[0]=[o('<h1 id="email-management" tabindex="-1">Email Management <a class="header-anchor" href="#email-management" aria-label="Permalink to &quot;Email Management&quot;">​</a></h1><p>The Email Management section provides tools to create and manage email accounts for your domains.</p><h2 id="email-accounts-overview" tabindex="-1">Email Accounts Overview <a class="header-anchor" href="#email-accounts-overview" aria-label="Permalink to &quot;Email Accounts Overview&quot;">​</a></h2><p><strong>URL:</strong> <code>/customer/email-accounts-page</code></p><p><img src="'+n+'" alt="Email Accounts Interface"></p><h3 id="interface-elements" tabindex="-1">Interface Elements <a class="header-anchor" href="#interface-elements" aria-label="Permalink to &quot;Interface Elements&quot;">​</a></h3><h4 id="top-bar" tabindex="-1">Top Bar <a class="header-anchor" href="#top-bar" aria-label="Permalink to &quot;Top Bar&quot;">​</a></h4><ul><li><strong>New email account</strong> button: Create new email accounts</li><li><strong>Search</strong> field: Filter accounts by name or domain</li><li><strong>Documentation</strong> link: Access detailed help resources</li></ul><h4 id="list-view-columns" tabindex="-1">List View Columns <a class="header-anchor" href="#list-view-columns" aria-label="Permalink to &quot;List View Columns&quot;">​</a></h4><ul><li><strong>Account @ Domain</strong>: Email address and associated domain</li><li><strong>Restrictions</strong>: Account status and limitations</li><li><strong>Storage</strong>: Used/Allocated space</li><li><strong>Actions</strong>: Available operations <ul><li>Login to Webmail</li><li>Manage Account</li><li>Connect Devices</li></ul></li></ul><h4 id="list-controls" tabindex="-1">List Controls <a class="header-anchor" href="#list-controls" aria-label="Permalink to &quot;List Controls&quot;">​</a></h4><ul><li><strong>Per page</strong> dropdown: Adjust number of visible accounts</li><li><strong>Sort options</strong>: Order by account name or domain</li><li><strong>Pagination</strong>: Navigate through multiple pages</li></ul><h3 id="quick-actions" tabindex="-1">Quick Actions <a class="header-anchor" href="#quick-actions" aria-label="Permalink to &quot;Quick Actions&quot;">​</a></h3><p>For each email account, you can:</p><ul><li><strong>Login to Webmail</strong>: Direct access to web interface</li><li><strong>Manage</strong>: Configure account settings</li><li><strong>Connect Devices</strong>: Set up email clients</li></ul><h2 id="creating-an-email-account" tabindex="-1">Creating an Email Account <a class="header-anchor" href="#creating-an-email-account" aria-label="Permalink to &quot;Creating an Email Account&quot;">​</a></h2><p><strong>URL:</strong> <code>/customer/email-accounts-page/create</code></p><p><img src="'+l+'" alt="Create Email Account Interface"></p><h3 id="required-information" tabindex="-1">Required Information <a class="header-anchor" href="#required-information" aria-label="Permalink to &quot;Required Information&quot;">​</a></h3><ol><li><p><strong>Username</strong></p><ul><li>Enter the local part of the email address (before @)</li><li>Will be combined with selected domain</li></ul></li><li><p><strong>Domain Selection</strong></p><ul><li>Choose from your available domains</li><li>Forms the complete email address (<a href="mailto:username@domain.com" target="_blank" rel="noreferrer">username@domain.com</a>)</li></ul></li><li><p><strong>Password Setup</strong></p><ul><li>Set password now (recommended)</li><li>Or set login link to alternate email address</li><li>Generate secure password option available</li><li>Password strength indicator included</li></ul></li></ol><h3 id="optional-settings" tabindex="-1">Optional Settings <a class="header-anchor" href="#optional-settings" aria-label="Permalink to &quot;Optional Settings&quot;">​</a></h3><p>Toggle the &quot;Optional Settings&quot; switch to configure:</p><ul><li>Storage quota</li><li>Spam filtering level</li><li>Auto-reply settings</li><li>Forwarding options</li></ul><h3 id="creation-options" tabindex="-1">Creation Options <a class="header-anchor" href="#creation-options" aria-label="Permalink to &quot;Creation Options&quot;">​</a></h3><ul><li><strong>Create</strong>: Set up single account</li><li><strong>Create &amp; create another</strong>: Create multiple accounts</li><li><strong>Cancel</strong>: Exit without creating</li></ul><h2 id="managing-email-accounts" tabindex="-1">Managing Email Accounts <a class="header-anchor" href="#managing-email-accounts" aria-label="Permalink to &quot;Managing Email Accounts&quot;">​</a></h2><p><strong>URL:</strong> <code>/customer/email-accounts-page/manage</code></p><p>From the email accounts list, you can:</p><ul><li>View account usage and storage statistics</li><li>Change passwords and security settings</li><li>Adjust quotas and restrictions</li><li>Configure spam filters and forwarding</li><li>Set up email clients and devices</li><li>Enable/disable account access</li></ul><h2 id="email-client-configuration" tabindex="-1">Email Client Configuration <a class="header-anchor" href="#email-client-configuration" aria-label="Permalink to &quot;Email Client Configuration&quot;">​</a></h2><p><strong>URL:</strong> <code>/customer/email-accounts-page/[account]/settings</code></p><p>Each email account provides:</p><ul><li>IMAP/POP3 settings</li><li>SMTP configuration</li><li>Webmail access</li><li>SSL/TLS security options</li><li>Authentication details</li></ul><h2 id="search-and-filter" tabindex="-1">Search and Filter <a class="header-anchor" href="#search-and-filter" aria-label="Permalink to &quot;Search and Filter&quot;">​</a></h2><p><strong>URL:</strong> <code>/customer/email-accounts-page</code> (with search parameters)</p><p>Use the search bar to quickly find specific email accounts by:</p><ul><li>Email address</li><li>Domain name</li><li>Account status</li></ul>',37)]))}const f=e(r,[["render",s]]);export{p as __pageData,f as default};
