import{_ as o,c as r,o as t,ag as a}from"./chunks/framework.Ds6Eueu6.js";const i="/assets/Customer_error-log.DOaUMp8x.png",m=JSON.parse('{"title":"Error Log – AdminBolt Hosting Panel","description":"","frontmatter":{},"headers":[{"level":3,"title":"Domain Error Log Info Box","slug":"domain-error-log-info-box","link":"#domain-error-log-info-box","children":[]},{"level":3,"title":"Log Table Columns","slug":"log-table-columns","link":"#log-table-columns","children":[]},{"level":3,"title":"Search & Filter Tools","slug":"search-filter-tools","link":"#search-filter-tools","children":[]},{"level":3,"title":"Log Management","slug":"log-management","link":"#log-management","children":[]},{"level":3,"title":"Pagination","slug":"pagination","link":"#pagination","children":[]}],"relativePath":"user-guide/customer-panel/error-log.md","filePath":"user-guide/customer-panel/error-log.md"}'),s={name:"user-guide/customer-panel/error-log.md"};function l(n,e,d,c,h,g){return t(),r("div",null,e[0]||(e[0]=[a('<h1 id="error-log-–-adminbolt-hosting-panel" tabindex="-1">Error Log – AdminBolt Hosting Panel <a class="header-anchor" href="#error-log-–-adminbolt-hosting-panel" aria-label="Permalink to &quot;Error Log – AdminBolt Hosting Panel&quot;">​</a></h1><p><img src="'+i+'" alt="Customer_error-log.png"></p><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p>The <strong>Error Log</strong> section in AdminBolt provides real-time visibility into domain-level errors reported by the web server. This includes HTTP-level errors, permission issues, misconfigurations, and other events that may impact the website’s functionality or availability.</p><h2 id="purpose" tabindex="-1">Purpose <a class="header-anchor" href="#purpose" aria-label="Permalink to &quot;Purpose&quot;">​</a></h2><ul><li>Debug site loading issues.</li><li>Identify permission and .htaccess-related problems.</li><li>Review server-side faults or unauthorized access attempts.</li><li>Monitor the impact of DNS, PHP, or misconfigured services.</li></ul><h2 id="interface-sections" tabindex="-1">Interface Sections <a class="header-anchor" href="#interface-sections" aria-label="Permalink to &quot;Interface Sections&quot;">​</a></h2><h3 id="domain-error-log-info-box" tabindex="-1">Domain Error Log Info Box <a class="header-anchor" href="#domain-error-log-info-box" aria-label="Permalink to &quot;Domain Error Log Info Box&quot;">​</a></h3><blockquote><p>“This error log displays errors related to your domains from the web server. It includes information about server-side issues, configuration problems, and other domain-related errors that may affect your website’s functionality.”</p></blockquote><p>This message clarifies the scope of the logs.</p><h3 id="log-table-columns" tabindex="-1">Log Table Columns <a class="header-anchor" href="#log-table-columns" aria-label="Permalink to &quot;Log Table Columns&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Column</th><th>Description</th></tr></thead><tbody><tr><td><strong>Time</strong></td><td>Timestamp of when the error occurred</td></tr><tr><td><strong>Source</strong></td><td>IP address initiating the request or causing the error</td></tr><tr><td><strong>Level</strong></td><td>Severity level (e.g., CRT for Critical)</td></tr><tr><td><strong>Message</strong></td><td>Descriptive server error message (e.g., permission denied, file not found)</td></tr></tbody></table><p><strong>Example message shown</strong>:<br><code>AH00529: /home/technode15718066201/myboltip/public_html/.htaccess pcfg_openfile: unable to check htaccess file...</code></p><h3 id="search-filter-tools" tabindex="-1">Search &amp; Filter Tools <a class="header-anchor" href="#search-filter-tools" aria-label="Permalink to &quot;Search &amp; Filter Tools&quot;">​</a></h3><ul><li><strong>Search bar</strong>: Allows users to find logs matching specific keywords.</li><li><strong>Filters</strong> button: Presumably offers filtering by time range, severity level, or IP address.</li></ul><h3 id="log-management" tabindex="-1">Log Management <a class="header-anchor" href="#log-management" aria-label="Permalink to &quot;Log Management&quot;">​</a></h3><ul><li><strong>Truncate Logs</strong> button: Clears the current log history. Useful to reset after debugging or before starting a new deployment.</li></ul><h3 id="pagination" tabindex="-1">Pagination <a class="header-anchor" href="#pagination" aria-label="Permalink to &quot;Pagination&quot;">​</a></h3><ul><li>Supports browsing through hundreds of log entries (834 total shown).</li><li>Default display: 10 entries per page.</li></ul><h2 id="use-cases" tabindex="-1">Use Cases <a class="header-anchor" href="#use-cases" aria-label="Permalink to &quot;Use Cases&quot;">​</a></h2><ul><li>Investigating 403/500 server errors.</li><li>Resolving permission issues in <code>.htaccess</code> or <code>public_html</code>.</li><li>Debugging failed redirects or mod_security rules.</li><li>Monitoring attacks or brute-force attempts from specific IPs.</li></ul><h2 id="sidebar-location" tabindex="-1">Sidebar Location <a class="header-anchor" href="#sidebar-location" aria-label="Permalink to &quot;Sidebar Location&quot;">​</a></h2><p><strong>Domains → Error Log</strong></p><h2 id="related-sections" tabindex="-1">Related Sections <a class="header-anchor" href="#related-sections" aria-label="Permalink to &quot;Related Sections&quot;">​</a></h2><ul><li><a href="./error-pages.html">Error Pages</a></li><li><a href="./dns-health.html">DNS Health</a></li></ul><h2 id="notes" tabindex="-1">Notes <a class="header-anchor" href="#notes" aria-label="Permalink to &quot;Notes&quot;">​</a></h2><ul><li>Provides essential information for developers, sysadmins, and hosting clients.</li><li>Especially valuable in shared hosting environments to isolate domain-specific issues.</li></ul>',27)]))}const p=o(s,[["render",l]]);export{m as __pageData,p as default};
