import{_ as i,c as l,o as a,ae as t}from"./chunks/framework.Dh1jimFm.js";const o="/screenshots/file-manager.png",f=JSON.parse('{"title":"File Manager","description":"","frontmatter":{},"headers":[],"relativePath":"user-guide/customer-panel/files.md","filePath":"user-guide/customer-panel/files.md"}'),r={name:"user-guide/customer-panel/files.md"};function n(s,e,d,c,g,h){return a(),l("div",null,e[0]||(e[0]=[t('<h1 id="file-manager" tabindex="-1">File Manager <a class="header-anchor" href="#file-manager" aria-label="Permalink to &quot;File Manager&quot;">​</a></h1><p>The File Manager provides a comprehensive interface for managing your hosting files and directories.</p><p><strong>URL:</strong> <code>/customer/file-manager-page</code></p><p><img src="'+o+'" alt="File Manager Interface"></p><h2 id="main-toolbar" tabindex="-1">Main Toolbar <a class="header-anchor" href="#main-toolbar" aria-label="Permalink to &quot;Main Toolbar&quot;">​</a></h2><h3 id="file-operations" tabindex="-1">File Operations <a class="header-anchor" href="#file-operations" aria-label="Permalink to &quot;File Operations&quot;">​</a></h3><ul><li><strong>New folder</strong>: Create new directories</li><li><strong>New file</strong>: Create blank files</li><li><strong>Copy</strong>: Copy selected items</li><li><strong>Cut</strong>: Move selected items</li><li><strong>Paste</strong>: Paste copied/cut items</li><li><strong>Upload files</strong>: Add files from your computer</li><li><strong>Download</strong>: Download selected items</li><li><strong>Delete</strong>: Remove selected items</li><li><strong>Rename</strong>: Rename selected item</li><li><strong>Edit file</strong>: Modify file contents</li><li><strong>Undo/Redo</strong>: Reverse or repeat actions</li></ul><h3 id="navigation-controls" tabindex="-1">Navigation Controls <a class="header-anchor" href="#navigation-controls" aria-label="Permalink to &quot;Navigation Controls&quot;">​</a></h3><ul><li><strong>Root</strong>: Return to home directory</li><li><strong>Go to parent folder</strong>: Move up one level</li><li><strong>Back/Forward</strong>: Navigate through history</li><li><strong>Reload</strong>: Refresh current view</li><li><strong>Select all/none</strong>: Bulk selection tools</li><li><strong>Empty the folder</strong>: Clear directory contents</li><li><strong>Icons view</strong>: Toggle view mode</li><li><strong>Preferences</strong>: Customize interface</li><li><strong>About</strong>: View software information</li></ul><h2 id="file-browser" tabindex="-1">File Browser <a class="header-anchor" href="#file-browser" aria-label="Permalink to &quot;File Browser&quot;">​</a></h2><h3 id="directory-tree-left-panel" tabindex="-1">Directory Tree (Left Panel) <a class="header-anchor" href="#directory-tree-left-panel" aria-label="Permalink to &quot;Directory Tree (Left Panel)&quot;">​</a></h3><ul><li>Expandable folder structure</li><li>Quick navigation between directories</li><li>Visual folder hierarchy</li><li>Special folders highlighted: <ul><li><code>.trash</code>: Deleted items</li><li><code>domains</code>: Website files</li><li><code>logs</code>: System logs</li><li><code>public_html</code>: Web root</li></ul></li></ul><h3 id="file-list-main-panel" tabindex="-1">File List (Main Panel) <a class="header-anchor" href="#file-list-main-panel" aria-label="Permalink to &quot;File List (Main Panel)&quot;">​</a></h3><p>Columns display:</p><ul><li><strong>Name</strong>: File/folder name with icon</li><li><strong>Mode</strong>: File permissions</li><li><strong>Modified</strong>: Last change date</li><li><strong>Size</strong>: File size</li><li><strong>Kind</strong>: File type</li></ul><h3 id="file-details" tabindex="-1">File Details <a class="header-anchor" href="#file-details" aria-label="Permalink to &quot;File Details&quot;">​</a></h3><p>For each item shows:</p><ul><li>File type icon</li><li>Name and extension</li><li>Last modification time</li><li>File size (for files)</li><li>Type description</li></ul><h2 id="search-and-filter" tabindex="-1">Search and Filter <a class="header-anchor" href="#search-and-filter" aria-label="Permalink to &quot;Search and Filter&quot;">​</a></h2><ul><li>Search box for finding files</li><li>Filter by file type</li><li>Sort by any column</li><li>Ascending/descending order</li></ul><h2 id="keyboard-shortcuts" tabindex="-1">Keyboard Shortcuts <a class="header-anchor" href="#keyboard-shortcuts" aria-label="Permalink to &quot;Keyboard Shortcuts&quot;">​</a></h2><p>Common operations:</p><ul><li><strong>Ctrl+C</strong>: Copy</li><li><strong>Ctrl+X</strong>: Cut</li><li><strong>Ctrl+V</strong>: Paste</li><li><strong>Delete</strong>: Remove item</li><li><strong>F2</strong>: Rename</li><li><strong>F5</strong>: Refresh</li></ul><h2 id="file-operations-1" tabindex="-1">File Operations <a class="header-anchor" href="#file-operations-1" aria-label="Permalink to &quot;File Operations&quot;">​</a></h2><h3 id="basic-actions" tabindex="-1">Basic Actions <a class="header-anchor" href="#basic-actions" aria-label="Permalink to &quot;Basic Actions&quot;">​</a></h3><ul><li>Create, delete, rename files</li><li>Create and manage folders</li><li>Copy and move items</li><li>Upload multiple files</li><li>Download files/folders</li></ul><h3 id="advanced-features" tabindex="-1">Advanced Features <a class="header-anchor" href="#advanced-features" aria-label="Permalink to &quot;Advanced Features&quot;">​</a></h3><ul><li>Edit text files</li><li>Change file permissions</li><li>Compress/extract archives</li><li>Manage file ownership</li><li>Set file attributes</li></ul>',28)]))}const m=i(r,[["render",n]]);export{f as __pageData,m as default};
